css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org"), domain("wikimedia.org") {


h2, 
.infobox th, 
[class*="navbox"] th, /*header boxes*/
#mw-panel, .infobox, tr.mergedtoprow >td,
.infobox.vcard [style^="background"],
.toccolours th[style*="background-color"],
.portal, /*portal boxes*/
#simpleSearch, #searchInput,
div[class*="source"], pre,
.thumbinner, .navbox-abovebelow,
.mw-search-formheader,
#catlinks.catlinks,
#mw-fr-revisiontag
{  -moz-border-radius: 4px;
   -webkit-border-radius: 4px;
}
table, .wikitable tbody,
.MainPageBG, #filetoc,
#contents-footer, 
div[style*="border: 1px solid rgb(238, 238, 238);"] 
{  -moz-border-radius: 6px;
   -webkit-border-radius: 6px;
}

.wikitable, .infobox { border-collapse: inherit !important;}

.wikitable tr:first-child th:first-child 
{  -moz-border-radius-topleft: 6px;
   -webkit-border-top-left-radius: 6px;
}
.wikitable tr:first-child th:last-child 
{  -moz-border-radius-topright: 6px;
   -webkit-border-top-right-radius: 6px;
}
.wikitable tr:last-child td:last-child,
.wikitable td[rowspan]:last-of-type
{  -moz-border-radius-bottomright: 4px;
   -webkit-border-bottom-right-radius: 4px;
}
.wikitable tr:last-child td:first-child,
.wikitable tr:last-of-type th,
table.collapsible tr:last-of-type td.navbox-group:last-of-type
{  -moz-border-radius-bottomleft: 4px;
   -webkit-border-bottom-left-radius: 4px;
}

#mw-fr-revisiondetails 
{  -moz-border-radius: 4px 0 4px 4px;
   -webkit-border-radius: 4px 0 4px 4px;
   -moz-box-shadow: 1px 1px 3px black;
   -webkit-box-shadow: 1px 2px 5px black;
   opacity: 0.9;
}

/*:: Fonts ::*/

/* (copy and paste the names where you want 'em) 
[[ Sans-serif: ]]
'Helvetica Neue', Arial, Helvetica,
'Bitstream Vera Sans', Verdana, 
'Droid Sans', 'Trebuchet MS',
'Lucida Grande', 'Lucida Sans Unicode', (NOT Lucida Sans)
'Franklin Gothic Book', 'Franklin Gothic Medium', 
'Gill Sans MT', Tunga,
'Bell Gothic Std Black',
[[ Geometric: ]]
'Kabel Book', 'Century Gothic', 'Tw Cen MT',
*/

h1.firstHeading, .mw-headline,
[style^="font-size: 162%;"] {
   font-family: 'Segoe UI Semibold', 'Bitstream Vera Sans', 
'Droid Sans', 'Trebuchet MS', Helvetica, Arial, sans-serif;
   font-weight: normal;
}

/*:: paragraph ::*/

p {font-family: Helvetica, 'Helvetica Neue', 'Franklin Gothic Book', Tahoma, 'Lucida Grande', 'Lucida Sans Unicode', Arial, sans-serif;}
p > b {font-family: Helvetica, Arial, sans-serif;}

/*:: lists ::*/

ul > li {font-family: Tahoma, 'Franklin Gothic Book', sans-serif;}
li > a {font-family: 'Droid Sans', 'Franklin Gothic Book', Tahoma, sans-serif;}
li > b {font-family: Gadget, 'Segoe UI Semibold', Trebuchet MS, sans-serif;}
li > i {font-family: 'Franklin Gothic Medium', sans-serif;}

pre, code, tt {font-family: monospace;}

}
