css:
@-moz-document url('http://www.olanet.ru'),
url('http://www.olanet.ru/'),
url('http://olanet.ru'),
url('http://olanet.ru/'), 
url('http://2ch.olanet.ru'),
url('http://2ch.olanet.ru/'),
url-prefix('http://olanet.ru/blog'),
url-prefix('http://olanet.ru/#')
{
	@CHARSET "UTF-8";

	a {
		color: #044983 !important;
	}

	a:hover {
		color: #0066FF !important;
	}

	body,html {
		background-image: none !important;
		background-color: #EEE !important;
		color: #333333 !important;
		font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
	}

	body, html, table {
		color: inherit !important;
		font-family: inherit !important;
		font-size: medium !important;
	}

	article {
		background: #ddd !important;
		border: 1px solid #CCC !important;
        border-collapse: separate !important;
		display:table !important;
		-webkit-border-radius: 5px important;
		-moz-border-radius: 5px !important;
		border-radius: 5px !important;
	}

	article article {
		border: 0px !important;
	}

	article blockquote {
		margin-top: 4px !important;
	}

	article div {
		min-width: 580px !important;

	}

	article header{
		background: inherit !important;
		border-top: 1px #CCC !important;
		background-image: none !important;
        font-size: 11px !important;
		padding-bottom: 4px !important;
	}

	article img {
		margin-top: 4px! important;
	}

	.app_content {
		margin-top: 0px !important;
		margin-bottom: 0px !important;
		margin-left: 16px !important;
		margin-right: 16px !important;

		padding-bottom: 8px !important; 
		padding-left: 8px !important; 
		padding-right: 8px !important;
	}
/*
	.app_content div {
		height: 1px !important;
	}
*/

	.app_content div a img {
		visibility: hidden !important;
	}

	.app_content .blog_content section img {
		visibility: visible !important;
	}

	.app_content .header {
		background-image: none !important;
		background-color: #CCC !important;
		border: 1px solid #BBB !important;
		clear: both !important;
		color: #26A !important;
		font-weight: bold !important;
		margin-bottom: 0.5em !important;
		padding: 2px !important;
		text-align: center !important;
		-webkit-border-radius: 3px important;
		-moz-border-radius: 3px !important;
		border-radius: 3px !important;
	}

	.blogPost_content {
		height: auto !important;
		border-left: 1px solid #777777 !important;
                padding-left: 4px !important;
                margin-left: 4px !important;
	}

	.blogPost_content div {
		background-color: inherit !important;
	}

	#latestPosts {
		width: 200px !important;
	}

	.olanet_content div {
		height: auto !important;
	}

	.olanet_content ul.postlist li .photo {

		height:32px !important;
		line-height:32px !important;
		width:32px !important;
	}

	.olanet_wrapper table {
		float: left !important;
	}

	.oppostpreview {
		background: inherit !important;
		background-image: none !important;
		float: left;
		padding: 10px;
		text-align: left !important;
	}

	.postlist {
		background-image: none !important;
		background-color: #CCC !important;
		border-bottom: 1px solid #777 !important; 
		border-left: 1px solid #777 !important;
		border-right: 1px solid #777 !important;
		border-top: 1px solid #777 !important;
	}

	.postlist li {
		background: #DDD !important;
		border-top: 0px !important;
		border-bottom: 0px !important;
	}

	.postlist div.li {
		height:33px !important;
	}

	.postlist div.li:hover {
		background-color: #CCC !important;
	}


	.postlist .li {
		border-bottom: 1px solid #BBB !important; 
		border-top: 0px !important;
	}

/*[[rightfixed]]*/

	section div {
		background: inherit !important;
	}

	.skip {
		background-image: none !important;
		background-color: inherit !important;
	}

	.spoiler {
		background: #BBBBBB !important;
		color: #333333 !important;
	}

	.spoiler:hover {
		color:#789922 !important;
	}

	.olanet_wrapper .toolbox {
		background-image: none !important;
		background-color: inherit !important;
		border-top: 0px !important;
		float: left !important;
		background: #CCC !important;
		border-left: 0px !important;
	}
	.olanet_wrapper .toolbox a {
		text-align: center !important;
		border-right: 1px solid #BBB !important
	}

	.olanet_wrapper table .toolbox {
		float: none !important;
		border-left: 0px !important;
		border-right: 0px !important;
		border-bottom: 0px !important;
		border-top: 0px !important;
		background: inherit !important;

	}

	.olanet_wrapper table .toolbox a {
		float: none !important;
		border-left: 0px !important;
		border-right: 0px !important;
		border-bottom: 0px !important;
		border-top: 0px !important;
		text-align: center !important;
		font-weight: bold !important; 
	}

	.olanet_wrapper table .toolbox a:hover {
		text-decoration:underline !important;
		color: #044983 !important;
	}

	.text span {	
		background: transparent !important;
		color: #024 !important;
		font-weight: bold !important;
	}

	.thread {
		background-image: none !important;	
		background-color: inherit !important;	
	}

	.thread {

	}

	.thread .header {
		margin-right: 2px !important;
		margin-left: 2px !important;
	}

	#bottom {
		height: 0px !important;
		padding: 0px !important;
		visibility: hidden !important;
	}

	#header {
		height: 0px !important;
		visibility: hidden !important;
	}

	#latestPosts {
		float:left !important;
	}


	#preview {
        background: none !important;
        border: 0px !important;
        height: auto !important;
	}

	#preview article {
        border: 1px solid #444 !important;
        height: auto !important;
	}

	#preview .info {
        visibility: hidden !important;
	}
}
