css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("4chan.org") {



/*background*/

html, body[bgcolor] {

  background-image:url(http://i32.tinypic.com/6ol66p.jpg) !important;

  background-position:right bottom !important;

  background-repeat:no-repeat !important;

  background-attachment:fixed !important;

  color: #000000 !important;}



/*Poster name*/

[class*="postername"] {

  color:#ffffff !important}



/*Poster trip*/

[class*="postertrip"] {

  color:#1384D4 !important}



/* Post size, shape, & color */

.reply, .replyhl {

  color: #ffffff !important;

  -moz-border-radius:7px !important;

  width: 450px !important;

  border-width: 1px !important;

  border-style: solid !important;

  border-color: #ffffff !important;

  background-color:#003366 !important;}



/*Thread Title*/

.filetitle, .replytitle {

  border-width: 1px !important;

  border-style: solid !important;

  border-color: #6699cc !important;

  background-color:#000000 !important;

  font-style:oblique !important;

  color:#006699 !important;}



/*Omited Posts*/

.omittedposts, .abbr {

  color: #000000 !important;}



/* Thread Dividers */

hr {

  display: none !important;}



/*Page Selection*/

table.pages {

  color: #000000 !important;

  border-style: solid !important;

  border-color: #444 !important;

  background-color:#000000 !important;

  opacity: 0.3;}



.pages td {

  color: #808080 !important;}



table.pages:hover {

  opacity:1 !important;}



/*image ads*/

img[src*="http://static.4chan.org/support/"], 

img[src*="http://affiliates.jlist.com/click/583?url=http://www.jlist.com/index.html"] {

  display:none !important;}



img[style="border:1px solid black;"] {

  display:none !important;}



.logo img {

  opacity:0 !important;}



center img, .bf {

  display:none !important;}



/*Removes "Posting mode: Reply"*/

html > body > table > tbody > tr > td,

html > body > table > tbody > tr > th {

  opacity: 0 !important; }



/*Removes "[Return]"*/

SPAN[style="left: 5px; position: absolute;"] {

  display:none !important;}



/*Removes "Rules" under the upload/comment box*/

.rules {

  display:none !important;}



/*Removes 4Chan Style links (Yotsuba, Yotsuba B, etc)*/

TD[align="right"] {

  display:none !important;}



/*Delete Post*/

.deletebuttons {

  position:absolute !important;

  right:15px !important;}



/* LOGO */

.logo > FONT:first-of-type > B > SPAN {

  position: absolute !important;

  right: 15px !important;

  top: 30px !important;

  font: 38px Tekton Pro !important;

  text-transform: lowercase !important;

  color: transparent !important; 

  text-shadow: 0px 0px 1px #EEE, 0px 0px 1px #01609F, 1px 1px 5px #A66 !important;

  cursor: default !important;

  -moz-user-select: none !important; }



.logo > FONT:nth-of-type(2) {

  position: absolute !important;

  right: 15px !important;

  top: 70px !important;

  text-align:right !important;

  width: 300px !important;

  color: #666 !important;

  text-transform: uppercase !important;

  cursor: default !important;

  -moz-user-select: none !important; }



/* Top Navigation Bar */

#navtop {

  position:fixed !important;

  left: 90px !important;

  background-color: #3D3D3D !important;

  opacity:0.2 !important;

  color: #808080 !important;}



#navtop:hover {

  opacity:1 !important;}



/* Remove bottom navigation bar and [Home / TW] */

#navbot, #navbotr {

  display:none !important;}



/*remove home link on top */

#navtopr {

  display:none !important;}



/* Removes Copyright */

#footer {

  display: none !important}



/* removes ugly borders on page overview [0,1,2...] */

table, td {

  border:none !important;}



/* Sets the color & other information of all links on the page */

a {

  color: #ffffff !important; }



/* As above, but for visited links */

a:visited {

  color:#1384D4 !important;

  opacity: 0.80 !important; }



/* Comment and File upload Box.*/



input, .inputtext, input[type="submit"], input[type="button"], input[type="file"]{

  -moz-border-radius-topright:7px !important;

  -moz-border-radius-bottomright:7px !important;

  border-width: 1px !important;

  border-style: solid !important;

  color: #ffffff !important; 

  background-color: #003366!important; 

  border: 1px solid #ffffff!important; 

  margin-right: 2px !important;

  -moz-appearance:none ;}



input[type="checkbox"]{

  background-color: #000000 !important;

  opacity: 0.5 !important}



div.postarea{

  position:fixed !important;

  width:500px !important;

  height:0px !important;

  opacity:0.1 !important;

  right:0px !important;

  bottom:400px !important;}



div.postarea:hover {

  opacity:1 !important;}



.postblock {

  -moz-border-radius-bottomleft:7px !important;

  -moz-border-radius-topleft:7px !important;

  background-color: #003366 !important;

  font-size:12px !important;

  color: #ffffff !important;}

}
