/* TuentiStyle por draco1989

- Author    : draco1989
- Created   : 21-5-09
- Usage     : Use with Stylish Firefox extension (http://userstyles.org/)
- Version   : v1.2

------------------------------------------------------------------------- */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tuenti.com") {

/* Generales*/

body {background: url(http://img816.imageshack.us/img816/8282/razer.jpg) #222222 fixed repeat left top !important; }

#main_info{background: #D2E5F1 !important; color:black !important;}

.item {background:#0a2d06; color:#259c00 !important}
.body{background:#000000 url(http://img139.imageshack.us/img139/4601/lineam.png) top repeat-x !important; color:white !important}
.body a:hover,.item a:hover{color:#000!important; background-color:#D2E5F1 !important}
.viewMore{background: #000000 !important; color:black !important}
.viewMore a,.viewMore a:visited{color:black !important}
.sidebar.left {background:black url(http://img139.imageshack.us/img139/4601/lineam.png) repeat-x scroll 0 0;height:1804px !important}
.left .mod {background-color:transparent !important;}
.sidebar .mod h2{background-color:#155e0d;}
.sidebar .mod.avatarMod, .sidebar .mod.homeInfo {background:none}
.left .mod .body {background-color:transparent !important}
.mod.wall .statusWall {background-color:transparent}

/* Vista foto*/

#breadcrumbs a{color:#dfdfdf!important; text-decoration:none !important; font-weight:bold !important}
#breadcrumbs a:hover{color:#000!important; background-color:#D2E5F1 !important}
#breadcrumbs a:visited{color:#dfdfdf!important; text-decoration:none !important; font-weight:bold !important}
.breadcrumbs,.breadcrumbs>.arrow,.breadcrumbs>.count{color:white !important;}

/* Perfil*/

#friends>.body {background:#0073b4; color:white !important; border:#00b2b4 1px solid !important}
blockquote p{color:#C0DCEB !important}
blockquote {background:#031e00 !important}
.informationList>dl>dt,.date {color:white !important}
blockquote>.author>.date {color:#00ffa0 !important}
#blog>.item {background:#0073b4; color:white !important; border:#00b2b4 1px solid !important}

}
