css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

body {
background: #242424 url("data:image/png;base64,") repeat !important;
color: #999 !important;
font: 13px/1.8 Tahoma, sans-serif !important;
min-height: 900px !important;
width: 1040px !important;
margin: 0 auto !important;
padding: 50px !important;
position: relative !important;
border: 4px solid #31363e !important;
-moz-border-radius: 36px 12px 36px 12px !important;
-moz-box-shadow: #000 0px 0px 75px !important;
margin-top: 30px !important;
}

html {
position: relative !important;
background: transparent url("data:image/png;base64,") repeat !important;
}

a {
color: #999 !important;
text-decoration: none !important;
outline: none !important;
}

p {
background: #31363e !important;
padding: 4px !important;
max-width: 50% !important;
-moz-border-radius: 6px !important;
-moz-box-shadow: #000 0px 0px 8px !important;
}

#donate input:hover {
-moz-box-shadow: #000 1px 1px 18px !important;
}

a:hover, li:hover {
color: #eee !important;
}

a:visited {
color: #999 !important;
opacity: .75 !important;
}

a:visited:hover {
color: #eee !important;
opacity: 1 !important;
}

li {
color: #333 !important;
}

h1, h2 {
background-color: #31363e !important;
margin-top: 20px !important;
text-shadow: #000 0px 0px 8px !important;
text-align: center !important;
-moz-border-radius: 16px 4px 16px 4px !important;
border: 2px solid #111 !important;
-moz-box-shadow: #000 0px 0px 8px !important;
}

#breadcrumbs, #other-links {
background: #020202 !important;
height: 34px !important;
margin-top: 20px !important;
font-size: 12px !important;
text-transform: uppercase !important;
}

#breadcrumbs, #other-links {
-moz-border-radius: 16px 4px 16px 4px !important;
text-align: center !important;
}

#breadcrumbs {
border: 2px solid #31363e !important;
-moz-box-shadow: #000 0px 0px 8px !important;
}

#breadcrumbs a, #breadcrumbs > * {
position: relative !important;
top: 6px !important;
padding: 6px !important;
}

#other-links {
margin-right: 50px !important;
width: 600px !important;
padding-right: 20px !important;
border: 1px solid #444 !important;
-moz-box-shadow: #000 0px 0px 8px !important;
border: 2px solid #31363e !important;
height: 40px !important;
position: relative !important;
top: -110px !important;
}


#breadcrumbs a:hover {
background: #31363e !important;
padding: 6px !important;
-moz-border-radius: 8px 3px 8px 3px !important;
}

#other-links a {
text-transform: uppercase !important;
text-decoration: none !important;
padding: 8px 8px 10px 8px !important;
-moz-border-radius: 3px !important;
margin-left: 6px !important;
position: relative !important;
top: 8px !important;
}

#other-links a:hover {
background: #31363e !important;
border: 2px solid #111 !important;
-moz-border-radius: 8px 3px 8px 3px !important;
padding: 8px 6px 10px 6px !important;
}

#sidebar ul {
background: #090909 !important;
padding: 24px !important;
-moz-border-radius: 16px 4px 16px 4px !important;
width: 400px !important;
margin-top: 8px !important;
border: 2px solid #31363e !important;
}

#sidebar ul a {
color: #aaa !important;
padding-bottom: 4px !important;
text-decoration: none !important;
}

#sidebar ul:hover {
background: #020202 !important;
padding: 24px !important;
-moz-box-shadow: #000 0px 0px 16px !important;
}

#sidebar ul li:hover {
color: #fff !important;
}

#sidebar ul li a:hover {
color: #fff !important;
}

#sidebar ul li a:visited {
color: #44617a !important;
}

#sidebar ul li a:visited:hover {
color: #eee !important;
}

#sidebar h2 {
text-transform: uppercase !important;
background: #31363e !important;
padding: 2px !important;
text-align: center !important;
margin-bottom: 4px !important;
-moz-border-radius: 16px 2px 16px 2px !important;
}

#sidebar h2 a {
text-decoration: none !important;
color: #999 !important;
}

#sidebar h2 a:visited {
color: #eee !important;
}

#sidebar h2 a:hover {
color: #eee !important;
}

/* Style Lists */
#style-list {
background: #090909 !important;
padding: 24px !important;
-moz-border-radius: 26px 4px 26px 4px !important;
border: 4px solid #31363e !important;
}

#style-list:hover {
background: #020202 !important;
padding: 24px !important;
-moz-box-shadow: #000 0px 0px 16px !important;
}

#style-list li:hover {
color: #fff !important;
background: transparent !important;
}

#style-list a:hover {
color: #eee !important;
}

#style-list a {
text-decoration: none !important;
}

/* Install Page */
#install {
background-color: #141414 !important;
border-color: #31363e !important;
-moz-box-shadow: #000 0px 0px 16px !important;
}

#install h2 {
font-weight: bolder !important;
}

pre#view-code {
background: #31363e !important;
color: #bbb !important;
-moz-border-radius: 6px !important;
border: 2px solid #777 !important;
word-wrap: break-word !important;
}

/*TEXTAREA CODE BOX ON STYLE PAGE - VERTICAL SCROLLABLE AND WHOLE BOX IS ALWAYS VIEWABLE*/
pre#view-code
{height:550px!important;
overflow-y: auto!important;}

/*WRAPS CODE*/
pre#view-code
{ white-space: pre-line !important; } 



ul#discussions {
background: #141414 !important;
padding: 8px !important;
-moz-border-radius: 16px 3px 16px 3px !important;
-moz-box-shadow: #000 0px 0px 8px !important;
border: 2px solid #31363e !important;
}

#discussions .good-rating, #discussions .bad-rating, #discussions .ok-rating, #discussions .no-rating {
margin-left: 26px !important;	
}

p a {
background: #111 !important;
padding: 3px !important;
-moz-border-radius: 8px 3px 8px 3px !important;
}

/* Discussion / Forum */
#Header, #Panel {
background: #141414 !important;
}

#Panel {
-moz-border-radius: 0px 0px 4px 4px !important;
}

#Panel h1 a, #Panel h1 a:visited {
color: #eee !important;
}

#Panel ul ul li a:hover {
background-color: #141414 !important;
}

#Panel h2 {
color: #eee !important;
}

#AboutVanilla {
background-color: #020202 !important;
-moz-border-radius: 0px 0px 4px 4px !important;
}

#Comments .CommentBody {
color: #bbb !important;
background: #31363e !important;
-moz-border-radius: 6px !important;
padding: 14px !important;
border: 4px solid #777 !important;
-moz-box-shadow: #000 0px 0px 16px !important;
width: 102% !important;
word-wrap: break-word !important;
}

#Comments .Commentbody p {
width: 75% !important;
}

#Comments .CommentBody a {
background: #111 !important;
-moz-border-radius: 8px 3px 8px 3px !important;
text-decoration: none !important;
padding: 3px !important;
}

#Comments .CommentBody a:visited {
text-decoration: none !important;
}

#Comments li {
border-color: #777 !important;
}

#Comments .CommentHeader span a:hover {
background: #31363e !important;
border: 1px solid #111 !important;
-moz-border-radius: 8px 3px 8px 3px !important;
-moz-box-shadow: #000 0px 0px 4px !important;
padding: 3px !important;
}

#Comments .CommentHeader li {
color: #777 !important;
}

#Comments .CommentHeader span a {
padding: 4px !important;
}

#Comments .CommentBody a {
text-decoration: underline !important;
}

#Comments .CommentHeader a {
text-shadow: #111 2px 2px 14px !important;
}

#Comments blockquote {
background-color: #141414 !important;
border: 2px solid #333 !important;
color: #eee !important;
-moz-border-radius: 8px 3px 8px 3px !important;
-moz-box-shadow: #000 0px 0px 8px !important;
margin-top: 8px !important;
}

#Comments blockquote cite {
color: #eee !important;
border-bottom: 2px solid #333 !important;
}

#CommentResults .SearchComment ul {
background-color: #393939 !important;
}

#CommentResults li ul {
border-color: #111 !important;
}

#CommentResults .SearchComment {
border-color: #111 !important;
}

#CommentResults .CommentText a:hover {
background-color: #111 !important;
}

#CommentResults .CommentText span.Highlight  {
background: #191919 !important;
padding: 3px !important;
-moz-border-radius: 3px !important;
}

.NoNewComments, #Discussions li.Read ul  {
background-color: #212121 !important;
}

#Discussions li li {
color: #eee !important;
}

.ContentInfo h1 {
color: #bbb !important;
}

.ContentInfo h1 a:visited, .ContentInfo h1 a {
color: #eee !important;
}

#Form form {
background-color: #191919 !important;
border-color: #777 !important;
}

#Form.Account label {
color: #ddd !important;
}
#Form.StartDiscussion label, #Form.AddComments label {
color: #eee !important;
}

#Form fieldset legend {
color: #eee !important;
background: #31363e !important;
-moz-border-radius: 16px 4px 16px 4px !important;
padding: 7px !important;
-moz-box-shadow: #000 0px 0px 8px !important;
border: 2px solid #777 !important;
}

#Form textarea, #Form input, #Form select {
background: #111 !important;
color: #ccc !important;
}

#Form label a {
background: #31363e !important;
border-color: #777 !important;
margin-bottom: 3px !important;
}

#Form li {
color: #eee !important;
background: #191919 !important;
}

#Header ul li.TabOn a:hover {
background-color: #31363e !important;
color: #eee !important;
border: none !important;
padding: 8px 10px 0px 10px !important;
}

#Header ul li.TabOn {
background-color: #31363e !important;
border: none !important;
background-color: transparent !important;
padding: 8px 10px 0px 10px !important;
}

#Header ul li.TabOn a {
background-color: #31363e !important;
border: 2px solid #777 !important;
-moz-box-shadow: #111 0px 0px 4px !important;
padding: 8px 10px 0px 10px !important;
color: #eee !important;
}

#Header ul li.TabOn a:visited {
color: #eee !important;
}

#Header ul li.TabOn a:hover {
border: 2px solid #777 !important;
-moz-box-shadow: #111 0px 0px 4px !important;
padding: 8px 10px 0px 10px !important;
}

#Header ul li a {
background-color: #777 !important;
-moz-border-radius: 8px 3px 8px 3px !important;
padding: 8px 12px 0px 12px !important;
color: #111 !important;
border: 2px solid #31363e !important;
}

#Header ul li a:hover {
background: #31363e !important;
padding: 8px 12px 0px 12px !important;
border: 2px solid #777 !important;
-moz-box-shadow: #111 0px 0px 4px !important;
color: #eee !important;
}

#Header ul li a:visited:hover {
color: #eee !important;
}

#Header ul li a:visited {
color: #111 !important;
}

#Header ul li {
border-bottom: none !important;
-moz-border-radius: 8px 3px 8px 3px !important;
padding: 8px 8px 0px 8px !important;
}

#Header h1 a, #Header h1 a:visited {
color: #999 !important;
}

#Header h1 a:hover {
color: #eee !important;
}

.NewComments, #Discussions li.Read.NewComments ul {
background: #555 !important;
}

#Discussions ul a {
color: #bbb !important;
}

#Discussions li ul {
color: #eee !important;
background-color: #292929 !important;
border-color: #777 !important;
margin-bottom: 12px !important;
}

#Discussions li ul:hover {
background-color: #020202 !important;
}

#Discussions ul a:visited {
color: #999 !important;
text-shadow: #000 0px 0px 4px !important;
}

#Discussions ul a:visited:hover {
color: #eee !important;
}

/* Categories */
#Categories li ul {
background: #141414 !important;
color: #bbb !important;
border-color: #777 !important;
}

#Categories li ul:hover {
background: #31363e !important;
}

#Categories li li {
color: #999 !important;
}

#CategoryPage div.ContentInfo h1 {
text-transform: uppercase !important;
background: #31363e !important;
text-align: center !important;
}

#Header a {
text-transform: uppercase !important;
}


#Header {
border-color: #777 !important;
}

/* Account */
#AccountProfile h2 {
color: #eee !important;
text-align: center !important;
-moz-border-radius: 8px 8px 0px 0px !important;
}

#AccountProfile h3, #AccountProfile p {
color: #bbb !important;
}

#AccountProfile h3:hover, #AccountProfile p:hover {
color: #eee !important;
}

#Session {
color: #eee !important;
}

.screenshot {
-moz-box-shadow: #000 0px 0px 18px !important;
border: 4px solid #31363e !important;
-moz-border-radius: 3px !important;
}

.pagination {
background: #020202 !important;
padding: 4px !important;
-moz-border-radius: 16px 4px 16px 4px !important;
border: 2px solid #31363e !important;
-moz-box-shadow: #000 0px 0px 8px !important;
}

.pagination a {
padding: 4px !important;
}

.pagination a:hover {
background: #31363e !important;
padding: 4px !important;
-moz-border-radius: 4px 2px 4px 2px !important;
}

.pagination .current {
background: #31363e !important;
padding: 4px !important;
-moz-border-radius: 4px 2px 4px 2px !important;
}

.multiple-screenshot {
max-width: 500px !important;   /* Comment to Set to Default */
}

.single-screenshot {
max-width: 500px !important;   /* Comment to Set to Default */
}

#Comments code {
background: #121212 !important;
-moz-border-radius: 8px !important;
border-color: #777 !important;
border-right: 6px solid #777 !important;
width: 700px !important;
margin-left: -8px !important;
}

pre {
background: #888 !important;
color: #111 !important;
border: 1px solid #eee !important;
-moz-border-radius: 8px !important;
}

.PageList a, .PageList li a, .PageList li {
padding: 8px !important;

}

.PageList {
height: 40px !important;
margin-top: 12px !important;
}

.PageInfo {
border-color: #777 !important;

}

.CurrentPage {
background: #31363e !important;
-moz-border-radius: 8px 3px 8px 3px !important;
border: 1px solid #111 !important;
-moz-box-shadow: #000 0px 0px 8px !important;
color: #eee !important;
padding: 7px !important;
}


.ContentInfo a:hover {
background: #31363e !important;
color: #eee !important;
-moz-border-radius: 8px 3px 8px 3px !important;
border: 1px solid #111 !important;
-moz-box-shadow: #000 0px 0px 8px !important;
padding: 7px !important;
}

.DiscussionTopic {
background: #31363e !important;
color: #eee !important;
-moz-border-radius: 8px 3px 8px 3px !important;
border: 2px solid #777 !important;
-moz-box-shadow: #000 0px 0px 8px !important;
text-align: center !important;
text-transform: uppercase !important;
}

#Discussions {
margin-bottom: 12px !important;
}

a#TopOfPage {
font-size: 1px !important;
margin-right: -50px !important
}

#obsoletion-message {
background: #111 !important;
-moz-box-shadow: #000 0px 0px 8px !important;
border: 2px solid #777 !important;
text-align: center !important;
font-size: 14px !important;
text-shadow: #ff0000 0px 0px 2px !important;
}

#stylish-installed-style-not-installed, #stylish-installed-style-installed  {
width: 200% !important;
margin-left: -4px !important;
}

/* ----------------------------------------------------------------------------------------- 8.29.10 ------------------------------------------------------------ */

#front-page-best, #front-page-newest {
background: #191919 !important;
padding: 6px !important;
-moz-border-radius: 6px !important;
border: 4px solid #31363e !important;
margin-right: 10px !important;
width: 450px !important;
-moz-border-radius: 16px 4px 16px 4px !important;
}

#front-page-best:hover, #front-page-newest:hover {
background: #141414 !important;
-moz-box-shadow: #000 0px 0px 8px !important;
}

#front-page-best li a:hover {
padding: 6px 6px 6px 10px !important;
background: #31363e !important;
margin-top: 6px !important;
-moz-border-radius: 0 6px 6px 0 !important;
border-left: 2px solid #eee !important;
}

#front-page-best li a {
padding: 6px !important;
margin-top: 6px !important;
}

#front-page-newest li a:hover {
padding: 6px 6px 6px 10px !important;
background: #31363e !important;
margin-top: 6px !important;
-moz-border-radius: 0 6px 6px 0 !important;
border-left: 2px solid #eee !important;
}

#front-page-newest li a {
padding: 6px !important;
margin-top: 6px !important;
}

/* ******************************* 2.7.11 ****************************** */

#left-sidebar > *, #left-sidebar, #left-sidebar li {
background-color: #31363e !important;
background: #31363e !important;
}

#left-sidebar {
-moz-border-radius: 16px 4px 16px 4px !important;
-moz-box-shadow: #000 0px 0px 8px !important;
border: 3px solid #020202 !important;
float: right !important;
margin-right: 150px !important;
margin-top: -120px !important;
width: 640px !important;
height: 40px !important;

}

#left-sidebar li:hover {
background: #020202 !important;
-moz-border-radius: 8px 2px 8px 2px !important;
color: #eee !important;
-moz-box-shadow: #000 0px 0px 8px !important;
}

#left-sidebar li {
text-align: center !important;
padding: 8px 16px 8px 16px !important;
display: inline !important;
}

#left-sidebar li a {
padding: 8px 16px 8px 16px !important;
}

body > footer p, body > footer {
text-align: center !important;
padding-top: 10px !important;
margin-left: 300px !important;
}

body > footer {
margin: 0 auto !important;
}

header {
background-color: transparent !important;
}



/* *************************  End 2.7.11 ****************************** */

