css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
body { 
  background: #CEE3F8 !important; 
}
.content {
  margin: 0 430px 0 100px !important;
  background-color: white !important;
  width: auto !important;
  padding: 15px !important;
  -moz-border-radius: 10px;
} 
.side {
  margin: -5px 100px 0 0 !important;
  background: transparent !important;
  width: 320px !important;
}
.side .spacer {
  background: white !important;
  padding: 10px !important;
  -moz-border-radius: 10px;

}
#header {
  border: none !important;
  background: transparent !important;
}

#sr-header-area {
  border: none !important;
  overflow: hidden !important;
  padding: 10px 5px !important;
  margin: 0 100px 0 100px !important;
  width: auto !important;
  -moz-border-radius: 0 0 10px 10px;
  background: white !important;
  text-transform: capitalize !important;
}

#sr-header-area .selected.title {
 color: orangered !important;
}

#sr-more-link {
  padding: 3px 15px !important;
  top: 7px !important;
  color: orangered !important;
  margin: 0 100px 0 0 !important;
  background: white !important;
}

#header-bottom-left {
  background: #CEE3F8 !important;
  margin: 10px 430px 0px 100px !important;
}
#header-img-a {
  
}

.tabmenu {
  width: 100% !important;
  margin: 20px 0 20px 0 !important;
}
.tabmenu li a {
  background: transparent !important;
  text-transform: capitalize !important;
  padding: 10px !important;
  color: #111 !important;
  -moz-border-radius: 10px !important;
}
.tabmenu li a:hover {
  text-decoration: underline !important;
}
.tabmenu li.selected a, .tabmenu li a:hover {
  color: orangered !important;
  background: white !important;
  border: none !important;
}
#header-bottom-right {
  margin: 0 100px 20px 0 !important;
  background: transparent !important;
  padding: 0 !important;
  width: 320px !important;
  font-weight: bold !important;
  text-align: right !important;
  border: none !important;
}

#header-bottom-right a {
  color: #111 !important;
}
.user {
  color: #77AADD !important;
}
.user a {
  max-width: 10px !important;
}

p.title a {
  color: #111 !important;
}

p.title a:hover {
  text-decoration: underline !important;
}
.tagline a, .flat-list a, .rank, .score {
  color: #77AADD !important;
}

.expando {

  padding: 10px !important;
  background: white;
  -moz-border-radius: 10px;
  border: 1px solid #EEE;

  -moz-box-shadow: 3px 3px 3px #999;
}

#siteTable .thing.link {
  margin: 0 !important;
  padding: 10px !important;
  border-bottom: 1px solid #EEE;
}

.entry {
  padding: 2px !important;
}

.drop-choices.inuse {
  padding: 10px !important;
  border: 1px solid #EEE !important;
  -moz-box-shadow: 3px 3px 3px #999;
  -moz-border-radius: 10px;
}

.sidecontentbox .content {
  margin: auto !important;
}

.content .searchpane, .content .infobar {
 margin: auto !important;
}

.footer-parent {
 margin-top: 50px !important;
 background: white !important;
}

.footer {
 border: none !important;
}

}
