@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bash.org.ru") {

     /*  Ð¡ÐºÑ€Ñ‹Ð²Ð°ÐµÐ¼ Ñ‡Ð°ÑÑ‚ÑŒ Ñ€ÐµÐºÐ»Ð°Ð¼Ñ‹ (Ð´Ð»Ñ Ð¿Ð¾Ð»Ð½Ð¾Ð³Ð¾ ÑÐºÑ€Ñ‹Ñ‚Ð¸Ðµ Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐ¹Ñ‚Ðµ AdBlock)  */
#bar, div[class*="news"], div[class*="y5"], a[href*="gogogo"], iframe[src*="ad.php"] {
	display: none !important;
}
div center {
	visibility: collapse !important;
}
div center div#cm_navi  {
	visibility: visible !important;
}
div center img#cm_strip  {
	visibility: visible !important;
}

     /*  Neutron  */
html, body {
	background-color: #212121 !important;
	color: #698CC0 !important;
	font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
}
a {
	color: #C9BE89 !important;
	text-decoration: none !important;
}
a:hover {
	color: #EEFEBB !important;
	text-shadow: #FFDE22 0px 0px 15px !important;
	text-decoration: none !important;
}
#navstrip {
	color: #48B0FD !important;
	background-color: #2C2C2C !important;
	border: 1px solid #575757 !important;
	-moz-border-radius: 0px 0px 8px 8px !important;
	-webkit-border-radius: 0px 0px 8px 8px !important;
	border-radius: 0px 0px 8px 8px !important;
	display: inline !important;
	padding: 0 20px 5px 20px !important;
}
.header {
	clear:both;
	text-align: left !important;
	font-size: 2em !important;
	font-weight: bold !important;
	color: #999999 !important;
	text-shadow: #000000 2px 0px 3px !important;
}
.header img, hr {
	display: none !important;
}
.menublock {
	background:#2C2C2C !important;
	padding: 5px !important;
	font-weight: bold !important;
	margin-right: 2em !important;
	border: solid 1px #515151 !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
}
.searchblock {
	color: #CCCCCC !important;
}
.searchblock input.button, input[type=submit] {
	color: #CCCCCC !important;
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	appearance: none !important;
	background:none repeat scroll 0 0 rgba(40, 40, 40, 0.5) !important;
	border: 1px solid #666666 !important;
	-moz-border-radius: 7px !important;
	-webkit-border-radius: 7px !important;
	border-radius: 7px !important;
	-moz-box-shadow:0 1px 1em 0 #666666 inset, -1px -1px 1em #000000, 1px 1px 1em #000000 !important;
	-webkit-box-shadow:0 1px 1em 0 #666666 inset, -1px -1px 1em #000000, 1px 1px 1em #000000 !important;
	box-shadow:0 1px 1em 0 #666666 inset, -1px -1px 1em #000000, 1px 1px 1em #000000 !important;
	margin-top: 5px !important;
	float: left !important;
}
input[type=submit] {
	float: right !important;
	margin-top: 0px !important;
}
.searchblock input.button:hover, input[type=submit]:hover {
	background: rgba(40,40,80,.5) !important;
	border: 1px solid #6699CC !important;
	-moz-box-shadow: inset #6699cc 1px 1px 1em 0px, #006699 -1px -1px 1em, #006699 1px 1px 1em !important;
	-webkit-box-shadow: inset #6699cc 1px 1px 1em 0px, #006699 -1px -1px 1em, #006699 1px 1px 1em !important;
	box-shadow: inset #6699cc 1px 1px 1em 0px, #006699 -1px -1px 1em, #006699 1px 1px 1em !important;
	color:#99CCFF !important;
	text-shadow: #99CCFF 0px 0px 1px, #FFFFFF 0px 0px 2px !important;
}
.pager {
	color: #CCCCCC !important;
	font-weight: bold !important;
}
.pager a {
	background: #111111 !important;
	border-top: solid 1px #575757 !important;
	border-bottom: solid 1px #575757 !important;
	border-left: none !important;
	border-right: none !important;
	font-weight: bold !important;
}
.pager a:last-child {
	background: transparent!important;
	border: none!important;
}
div.q div.vote {
	background: url("data:image/png;base64,") left center  no-repeat #2C2C2C !important;
	border: 1px solid #575757 !important;
	border-bottom: 0px !important;
	-moz-border-radius: 5px 5px 0px 0px !important;
	-webkit-border-radius: 5px 5px 0px 0px !important;
	border-radius: 5px 5px 0px 0px !important;
}
div.q div {
	background-color: #2C2C2C !important;
	border: 1px solid #575757 !important;
	border-top: 0px !important;
	-moz-border-radius: 0px 0px 5px 5px !important;
	-webkit-border-radius: 0px 0px 5px 5px !important;
	border-radius: 0px 0px 5px 5px !important;
}
div.q div.vote a:first-child {
	font-size: .8em !important;
	font-weight: bold !important;
	color: #C9BE89 !important;
}
div.q div.vote a:first-child:before {
	content:"â„–" !important;
}
div.q div.vote a[onclick*=", 0,"] {
	color: #789922 !important;
	font-weight: bold !important;
}
div.q div.vote a[onclick*=", 0,"]:hover {
	text-shadow: none !important;
}
div.q div.vote a[onclick*=", 1,"] {
	font-weight: bold !important;
	color: #AA3333 !important;
}
div.q div.vote a[onclick*=", 1,"]:hover {
	text-shadow: none !important;
}
small {
	display: block !important;
	visibility: hidden !important;
}
input[type="text"] {
	-moz-box-sizing: content-box !important;
	-webkit-box-sizing: content-box !important;
	box-sizing: content-box !important;
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	appearance: none !important;
	background-color: #111111 !important;
	color: #FFFFFF !important;
	border: 2px solid #545454 !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
}
input:hover[type="text"], input:focus[type="text"] {
	background-color: #151515 !important;
	color: #AAAAFF !important;
}
div.vote {
	padding-left: 25px !important;
}
#cm_navi a:hover, td.navi a:hover {
	background-color: #151515 !important;
}
div.q div form {
	background: none !important;
}
div.aby {
	background-color: transparent !important;
	border: none !important;
}
div.aby a:hover {
	background-color: transparent !important;
}

}
