css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(boards.4chan.org), domain(images.4chan.org), domain(sys.4chan.org) {
html, body[bgcolor] {
  background-repeat: no-repeat !important;
  background-attachment: fixed !important;
  background-color: #000000 !important;
  color: #888888 !important;
  font-family: calibri, sans-serif !important;
  font-size: 14px !important;
}
.reply, .replyhl {
opacity: 0.85 !important;
}
[class*="postername"] span, [class*="postername"] {
  color: #1384D4 !important;
}
[class*="postertrip"] {
  color: #005592 !important;
}
table tr:first-child td + td[class="reply"] {
  width: 600px !important;
}
.reply, .replyhl {
  color: #888888 !important;
  -moz-border-radius: 12px !important;
  border-width: 1px !important;
  border-style: solid !important;
  border-color: #444444 !important;
  background-color: #1C1C1C !important;
  font-size: 14px !important;
}
.replyhl {
  border-color: #ffffff !important;
  border-style: dotted !important;
  border-width: 2px !important;
  width: 650px !important;
  background-color: #333333 !important;
}
blockquote {
font-size: 14px !important;
}
font.unkfunc {
  font-style: italic !important;
}
.spoiler {
  border: 1px solid #1384D4 !important;
  padding: 2px !important;
}
img[hspace="20"] {
padding-bottom: 10px !important;
}
.filetitle, .replytitle {
  font-style: italic !important;
  color: #ffffff !important;
}
.omittedposts, .abbr {
  color: #005592 !important;
  font-style: italic !important;
  font-size: 12px !important;
}
table.pages {
  color: #808080 !important;
  border-style: solid !important;
  border-color: #444 !important;
  border-width: 1px !important;
  background-color: #1C1C1C !important;
  opacity: 0.3 !important;
}
.pages td {
  color: #808080 !important;
}
.pages td b {
  color: #ffffff !important;
}
table.pages input {
  background-color: #111111 !important;
  -moz-border-radius: 7px !important;
}
table.pages:hover {
  opacity: 1 !important;
}
.logo img {
  opacity: 0.8 !important;
  border-color: #1384D4 !important;
}
table[align="right"]:hover {
  opacity: 1 !important;
}
table[align="right"] {
  opacity: 0.3 !important;
}
.logo > font:first-of-type > b > span {
  font: 38px calibri !important;
  color: #ffffff !important;
  text-shadow: 0 0 0.2em #1384D4, 0 0 0.2em #1384D4, 0 0 0.5em #1384D4 !important;
  cursor: default !important;
  -moz-user-select: none !important;
}
.logo > font:nth-of-type(2) {
  text-align: right !important;
  width: 300px !important;
  color: #666 !important;
  text-transform: uppercase !important;
  cursor: default !important;
  -moz-user-select: none !important;
}
.logo {
  margin-bottom: 10px !important;
}
#navtop {
  background-color: #000000 !important;
  color: #808080 !important;
  width: 100% !important;
  text-align: center !important;
  font-size: 11px !important;
  opacity: 0.6 !important;
  padding: 2px 0 !important;
}
#navtop:hover {
  opacity: 1 !important;
}
#footer span#navbot {
  width: 100% !important;
  text-align: center !important;
  background-color: black !important;
  opacity: 0.3 !important;
  color: #808080 !important;
  font-size: 11px !important;
  padding: 2px 0 !important;
}
#footer span#navbot:hover {
  opacity: 1 !important;
}
hr, #navtopr, #footer #navbotr, #footer center, TD[align="right"], .rules, SPAN[style="left: 5px; position: absolute;"], center img, .bf, html > body > table > tbody > tr > td,
html > body > table > tbody > tr > th, img[style="border:1px solid black;"], img[src*="http://static.4chan.org/support/"], img[src*="http://affiliates.jlist.com/click/583?url=http://www.jlist.com/index.html"] {
  display: none !important;
}
a {
  color: #1384D4 !important;
  text-decoration: none !important;
}
a:visited {
  color: #1384D4 !important;
}
input, .inputtext, input[type="submit"], input[type="button"], input[type="file"]{
  -moz-border-radius-topright:7px !important;
  -moz-border-radius-bottomright:7px !important;
  color: #535353 !important;
  background-color: #000000 !important;
  border: 1px solid #1C1C1C !important;
  margin-right: 2px !important;
  -moz-appearance: none !important;
}
input[type="checkbox"]{
  opacity: 0.5 !important;
}
div.postarea{
  width: 500px !important;
  margin: 0 auto 0 auto !important;
}
.postblock {
  -moz-border-radius-bottomleft: 7px !important;
  -moz-border-radius-topleft: 7px !important;
  background-color: #1C1C1C !important;
  font-size: 10px !important;
  color: #1384D4 !important;
  text-align: center !important;
  border: 1px solid #000000 !important;
}
#twi_table_div {
  border: none !important
}
#watchedthreadsdraghandle, #twi_table_div > .postblock {

  color:#ffffff !important;
  border: 1px solid #000000 !important;
  border-bottom: none !important;
  -moz-border-radius: 12px 12px 0 0 !important;
  padding: 2px 0 !important;
}
#twi_table {
  -moz-border-radius: 0 0 12px 12px !important;
  border: 1px solid #000000 !important;
  padding: 2px !important;
}
.hidethread, .unhidethread, .watchthread, .expandthread, .quickreply, .hidewatchedthreads, .refreshwatchedthreads, .restorewatchedthreads, * html .hidethread, .unhidethread, .watchthread, .expandthread, .quickreply, .hidewatchedthreads, .refreshwatchedthreads, .restorewatchedthreadsimg, img[src^="chrome://4chan/skin/buttons/"], img[alt="Locked"], img[alt="closed"] {
  background-image: url('http://img204.imageshack.us/img204/6361/iconsc.png') !important;
}
img[src^="chrome://4chan/skin/buttons/"], img[alt="Locked"], img[alt="closed"] {
  height: 0 !important;
  margin: 0 2px !important;
  padding-top: 16px !important;
  width: 16px !important;
  background-color: transparent !important;
}
[src$="post_expand_plus.png"] {
  background-position: -48px 0 !important;
}
[src$="arrow_up.png"] {
  background-position: -64px 0 !important;
}
[src$="quote.png"] {
  background-position: -80px 0 !important;
}
[src$="stop.png"] {
  background-position: -96px 0 !important;
}
[src$="arrow_right.png"] {
  background-position: -112px 0 !important;
}
[src$="post_expand_minus.png"] {
  background-position: -32px -16px !important;
}
[src$="arrow_down.png"] {
  background-position: -64px -16px !important;
}
[src$="arrow_down2.png"] {
  background-position: -80px -16px !important;
}
[src$="empty.png"] {
  background-position: -96px -16px !important;
}
[id^="4chan_ext"][class="reply"], .reflinkpreview {
background: transparent !important;
border: 0px !important;
}
img[alt="Locked"], img[alt="closed"] {
background-position: -112px -16px !important;
}}
/*Azures*/
@-moz-document domain(boards.4chan.org) {
html, body[bgcolor] {
  background-image: url('http://a.imageshack.us/img8/340/spyco.png') !important;
  background-position: top right !important;
}}
@-moz-document domain(sys.4chan.org) {
html, body[bgcolor], body {
  background-image: url('http://a.imageshack.us/img535/6519/1279091761752.png') !important;
  background-position: top center !important;}
#footer {
  display: none !important;
}
table[width="100%"] {
  background-color: #333333 !important;
  -moz-border-radius: 12px !important;
  padding: 5px !important;
  color: #888888 !important;
}
table[width="100%"] input[type="submit"] {
  color: #ffffff !important;
  -moz-border-radius: 7px !important;
  margin: 5px 0 0 0 !important;
}}
@-moz-document domain(images.4chan.org) {
html, body[bgcolor] {
  background-image: none !important;
}}
