/******************************************/
/************* Arch Linux KISS ************/
/*********** Created by: tjwoosta *********/
/******************************************/

/*******************************************
 
To change the colors of this style use your
text editors global search and replace
feature to replace the following colors.

#262626 (background color)
#3e3e3e (border color)
#8a8a8a (text color)
#a7a15e
#a67b07
#556b2f
#af5f00
#705b46
#f0e68c
#7b3303

********************************************/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("archlinux.org") {


* {
background: #262626 !important;
border-color: #3e3e3e !important;
color: #8a8a8a !important;
}

input,
button,
select,
textarea {
background: #262626 !important;
border: 1px solid #3e3e3e !important;
}

ul {
list-style-image: none !important;
}

div#archnavbar {
border-color: #262626 !important;
}

div#archnavbar * {
padding: 5px !important;
}

div.postbody,
div.postright {
border-color: #262626 !important;
}

.pun .linksb .crumbsplus .pagepost,
.pun .postlinksb .crumbsplus .pagepost {
border-bottom: none !important;
border-top: 1px solid #3e3e3e !important;
}

.pun .linkst .crumbsplus .pagepost {
border: none !important;
}

.icon {
border-color: #3e3e3e !important;
}

.icon-new,
.inew {
border-color: #af5f00 !important;
}

.end-box,
.end-box *,
.top-box,
.top-box * {
background: none !important;
}

#onlinelist,
#brdstats,
#brdmenu *,
#quickpost,
#brdmain {
border: none !important;
}

p.timestamp,
span.conr,
span.byuser {
color: #705b46 !important;
}

ul li *,
ul li {
color: #a67b07 !important;
}

h1,
h1 *,
h2,
h2 *,
h3,
h3 *,
h4,
h4 *,
h5,
h5 *,
h6,
h6 * {
color: #556b2f !important;
}

