css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("beautyplanets.web.fc2.com") {



#rakuten,.amazon,.dmm,
iframe[height="145px"],iframe[height="150"]{
display:none!important;
}

div[style="border: 1px solid rgb(0, 0, 0); width: 120px; height: 600px;"]{
border: 0px!important;
}

#main,#menu,ul.acc,ul.acc li ul {
background-color:#fff!important;
}

#menu{
margin:0 0 0 100px!important;
}

#main{
margin:0 0 0 50px!important;
width:750px!important;
}

h2 {
background-color:#eee!important;
color:#0c0c0c!important;
border-color:#aaa!important;
border-width:0px 0px 0px 15px!important;
}

ul.acc a {
color:#0c0c0c!important;
}

table,td,tr{
border:none!important;
}

td{
background-color:#eee!important;
color:#0c0c0c!important;
font-size:17px!important;
font-weight:bold!important;
padding:8px!important;
}

a:link {
color:#0c0c0c!important;
}

a:visited {
color:#0c0c0c!important;
}

a:hover {
color:#0000FF!important;
}

a:active {
color:#0c0c0c!important;
}

}
