@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bash.org.ru") {

     /*  Ð¡ÐºÑ€Ñ‹Ð²Ð°ÐµÐ¼ Ñ‡Ð°ÑÑ‚ÑŒ Ñ€ÐµÐºÐ»Ð°Ð¼Ñ‹ (Ð´Ð»Ñ Ð¿Ð¾Ð»Ð½Ð¾Ð³Ð¾ ÑÐºÑ€Ñ‹Ñ‚Ð¸Ðµ Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐ¹Ñ‚Ðµ AdBlock)  */
#bar, div[class*="news"], div[class*="y5"], a[href*="gogogo"], iframe[src*="ad.php"] {
	display: none !important;
}
div center {
	visibility: collapse !important;
}
div center div#cm_navi  {
	visibility: visible !important;
}
div center img#cm_strip  {
	visibility: visible !important;
}

     /*  Photon  */
html, body {
	background-color: #EEEEEE !important;
	color: #333333 !important;
	font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
}
a {
	color: #FF6600 !important;
}
a:hover {
	color: #0066FF !important;
}
#navstrip {
	background-color: #DDDDDD !important;
	border: 1px solid #CCCCCC !important;
	-moz-border-radius: 0px 0px 8px 8px !important;
	-webkit-border-radius: 0px 0px 8px 8px !important;
	border-radius: 0px 0px 8px 8px !important;
	display: inline !important;
	padding: 0 20px 5px 20px !important;
}
.header {
	clear:both;
	text-align: left !important;
	font-size: 2em !important;
	font-weight: bold !important;
	color: #FF6600 !important;
}
.header img, hr {
	display: none !important;
}
.menublock {
	background:#DDDDDD !important;
	padding: 5px !important;
	font-weight: bold !important;
	margin-right: 2em !important;
	border: solid 1px #CCCCCC !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
}
.searchblock input.button {
	float: left !important;
}
.pager a {
	background: #DDDDDD !important;
	border: solid 1px #CCCCCC !important;
	font-weight: bold !important;
}
.pager a:last-child {
	background: transparent!important;
	border: none!important;
}
div.q div.vote {
	background: url("data:image/png;base64,R0lGODlhFAAPAMIDAEBAQICAgNTQyP///////////////////yH+EUNyZWF0ZWQgd2l0aCBHSU1QACH5BAEKAAQALAAAAAAUAA8AAAM4SLrc3iHKGccLIOstrMNDKIrdBYxo+Z0o6UFsO6iwHNIMaOOLLvMKXwtIEKZeOYFyySTaUI+oIwEAOw==") left center  no-repeat #DDDDDD !important;
	border: 1px solid #CCCCCC !important;
	border-bottom: 0px !important;
	-moz-border-radius: 5px 5px 0px 0px !important;
	-webkit-border-radius: 5px 5px 0px 0px !important;
	border-radius: 5px 5px 0px 0px !important;
}
div.q div {
	background-color: #DDDDDD !important;
	border: 1px solid #CCCCCC !important;
	border-top: 0px !important;
	-moz-border-radius: 0px 0px 5px 5px !important;
	-webkit-border-radius: 0px 0px 5px 5px !important;
	border-radius: 0px 0px 5px 5px !important;
}
div.q div.vote a:first-child {
	font-size: .8em !important;
	font-weight: bold !important;
	color: #000000 !important;
}
div.q div.vote a:first-child:before {
	content:"â„–" !important;
}
div.q div.vote a[onclick*=", 0,"] {
	color: #008822 !important;
}
div.q div.vote a[onclick*=", 1,"] {
	color: #880000 !important;
}
small {
	display: block !important;
	visibility: hidden !important;
}
div.vote {
	padding-left: 25px !important;
}
#cm_navi a:hover, td.navi a:hover {
	background-color: #EEEEEE !important;
}
div.aby {
	background-color: transparent !important;
	border: none !important;
}
div.aby a:hover {
	background-color: transparent !important;
}

