css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tiny.4chan.org") {
* {
margin:0;
padding:0;
}
body {
background-color: #000;
background-image: url(http://cdn.store.steampowered.com/public/images/v5/content_bg.png);
background-repeat: repeat-x;
color:#D8DED3;
font-family:Arial,sans-serif;
font-size:12px;
padding:0;
}
img {
border:medium none;
}
abbr {
cursor:help;
}
a {
color:#D8DED3;
}
a:hover {
text-decoration:none;
}
#notice {
background-color:#63605E;
padding:0.2em;
}
h1 {
display:none;
font-size:17px;
text-align:center;
}
h1 a {
color:#000000;
text-decoration:none;
}
h2 {
font-size:18px;
margin-bottom:0.3em;
margin-left:1em;
margin-top:2.3em;
}
h2 a {
text-decoration:none;
}
h2 a:hover {
text-decoration:underline;
}
p {
margin:1em;
}
p.caption {
font-size:11px;
margin-top:2px;
}
p.head {
background-color:#63605E;
margin:0 1em;
overflow:auto;
padding:0.5em;
}
.post_main {
float:left;
}
.post_ID {
float:right;
text-align:right;
vertical-align:middle;
}
#error {
font-weight:bold;
}
h3 {
font-size:14px;
margin:0 0 0.5em;
}
h4.section {
font-size:15px;
margin:0 1em 1em;
}
.body {
background-color:#3A3A3A;
line-height:1.5em;
margin-bottom:0.5em;
margin-left:1em;
margin-right:1em;
overflow:hidden;
padding-left:0.5em;
padding-top:0.3em;
}
.body a {
color:#000000;
}
.spoiler {
background-color:#D8DED3;
color:transparent;
}
.spoiler:hover {
color:#314159;
}
.underline {
text-decoration:underline;
}
.quote {
color:#FFFFFF;
}
.border {
background-color:#63605E;
border:3px dashed #D8DED3;
margin:1em;
padding:0.5em;
}
.standalone {
margin-bottom:1em;
}
.highlighted {
background-color:#63605E;
}
ul, ol {
margin-left:4em;
}
ul.menu {
padding:0;
}
.body ul.menu {
clear:both;
margin:0.5em 0 0;
text-align:right;
}
.body ul.menu li {
margin:0 0 0 0.5em;
}
.body ul.menu li, .body ul.menu li a, .body ul.menu li span {
font-size:10px;
}
.body img.thumbnail {
float:left;
margin:6px 9px 6px 6px;
}
.split {
float:left;
width:50%;
}
label {
font-weight:bold;
}
label.common {
float:left;
margin-right:0.5em;
text-align:right;
width:15em;
}
input, textarea {
display:block;
}
input[type="text"] {
font-family:monospace;
padding:1px;
}
textarea {
font-family:monospace;
margin:0 0 1em;
padding:2px;
width:100%;
}
input[type="submit"] {
margin-right:4px;
padding:1px;
}
input[type="text"]:focus, input[type="password"]:focus, input[type="file"]:focus, textarea:focus {
background-color:#FFF6D1;
outline-color:#E8C177;
outline-style:solid;
outline-width:1px;
}
input[type="file"] {
margin-top:0.5em;
width:100%;
}
input[type="checkbox"] {
margin:0 3px;
}
fieldset {
margin:1em;
}
legend {
font-weight:bold;
}
ul.body, ul.error_message {
list-style-type:none;
}
ul.error_message {
margin:0;
padding:0;
}
ul.error_message li {
font-style:italic;
padding:0.2em;
}
ul.menu {
margin:1em;
text-align:center;
}
ul.menu li {
display:inline;
font-weight:bold;
margin:0 1em;
}
ul#main_menu {
background-color:#63605E;
margin:0;
padding:0.8em;
position:fixed;
top:0;
left:0;
width:100%;
}
ul.stuff {
margin-bottom:2em;
}
ul.stuff li {
margin-bottom:0.5em;
}
table {
border-collapse:collapse;
margin-bottom:1em;
margin-left:1em;
margin-right:1em;
}
table th {
background-color:#63605E;
padding:0.5em;
text-align:left;
}
table td {
background-color:#1E1E1E;
padding:0.5em;
vertical-align:top;
}
table tr.odd td {
background-color:#191919;
}
table tr:hover td {
background-color:#314159;
}
table .minimal {
white-space:nowrap;
width:1%;
}
.reply_body_snippet, .snippet {
font-style:italic;
}
.topic_headline a, .reply_body_snippet a, .snippet a {
font-weight:bold;
text-decoration:none;
}
.topic_headline a:visited {
font-weight:normal;
}
em.marked {
background-color:#63605E;
font-style:normal;
font-weight:bold;
}
.unimportant, .unimportant * {
font-size:10px;
}
.row {
margin:0 1em 1em;
}
.inline {
display:inline;
}
.noscreen {
display:none;
}
.help {
cursor:help;
}
#memorable_name {
margin-bottom:0.5em;
}
#hot_topics li {
line-height:1.5em;
}
#hot_topics a {
font-weight:bold;
}
#hot_topics a:visited {
font-weight:normal;
}
#last_seen_marker {
border-top:2px solid #494949;
}
#snapback_link {
font-size:18px;
font-weight:bold;
left:4px;
position:fixed;
top:50%;
}
#snapback_link a {
text-decoration:none;
}
.center {
margin:auto;
text-align:center;
}
.advertisment, .advertisment:hover {
background-color:#494949;
font-size:10px;
padding:7px;
text-align:center;
margin-left:1.1em;
margin-right:1.1em;
margin-bottom:0.5em;
}
}
