css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

body, table td, input, textarea, font, div#updater input, span.postername ~ blockquote { font-family:  Georgia, "Meiryo UI", "Times New Roman", Serif !important; font-size: 12px !important; }
span#navtop, span#navbot { font-size: 10px !important; padding: 3px; z-index: 10; }

div.postarea td.postblock { line-height: 0px !important; }
div.postarea { padding: 0 !important; }
td.postblock { border: 0 !important; }
input[type=submit], input[type=file] { font-size: 11px !important; }
input[type=checkbox] { margin-top: 0px !important; }

input.inputtext, textarea[name=com] { border: 1px #111 solid !important; }

textarea[name=com] { width: 100%;  height: 100px !important; }

hr[size] { display: none; }

body, div.logo, div.postarea, div#qr { background: #eee !important; }
div#qr { padding: 5px; }

div#header { text-align: center; }
span#navtop { position: fixed; top: 0px;  left: 0px; width: 100%; background: #111; color: #eee; }
span#navtop a { color: #eee !important; font-weight: bold !important; }

a { text-decoration: none !important; border-bottom: 1px dotted #777; color: #333 !important;  }
a:hover { text-decoration: underline !important; }

img.pointer { padding-top: 5px !important; }

span.filesize ~ a > img { margin-top: 5px !important; border: 1px #aaa solid !important; }

hr { border-color: #ddd !important; }

td.postblock { background: #111 !important; color: #eee !important; }

td.reply, td.replyhl { padding-right: 3px !important; } 
td.reply, div#backwash_tooltip { background: #f5f5ee !important; border: 1px #111 solid !important; }
td.replyhl { background: #e8e8e8 !important; border: 1px #111 solid !important; }
td.reply, td.replyhl, div#backwash_tooltip, div#watcher, div#updater, div#thread_filter, table.pages, div#qr { -moz-border-radius: 5px !important; }
blockquote table { padding-top: 1px; }

.backlink { font-weight: bold; font-size: 11px; }
a.backlink + a.pointer { margin-left: 3px; }
a.backlink + a.backlink { margin-left: 1px; }

center + hr, script + center, span#navtopr, span#navbotr, br + center font[size="2"] { display: none !important; }
hr + table th { background: #ddd !important; border: 1px #999 solid; }

a.forwardlink { font-weight: bold; font-size: 11px; }

font.unkfunc { color: #959595 !important }

font.unkfunc + br { margin-bottom: 5px !important; }

span.postername, span.commentpostername, span.postertrip { color: #1b1717 !important; }
span.filetitle { color: #6d6161 !important; }

form > blockquote, td.reply blockquote, td.replyhl blockquote, div#backwash_tooltip blockquote { font-size: 11px !important; }
td.reply blockquote, td.replyhl blockquote, div#backwash_tooltip blockquote, td:not([class=postblock]) { color: #111 !important; margin-left: 15px !important; }
div#backwash_tooltip td, td.rules, td.reply, td.replyhl, form, form > *, hr + table font, div.logo, span#navbot, table + span, td > font > b { color: #111 !important; }

table.pages { background: #e9e9e9 !important; border: 1px #111 solid !important; }
table.pages td { color: #111 !important; }

div#watcher, div#thread_filter, div#updater { color: #111 !important; background: #e9e9e9 !important; font-size: 10px !important; }
div#thread_filter { padding: 3px; border: 1px #111 solid; }
div#thread_filter, div#thread_filter:hover { border: 1px #111 solid !important; }
div.move span#stat.new { background: #f6f6f6 !important; padding: 2px; border: 1px #111 dotted; }
div#updater input { font-size: 11px !important; }

a.pointer + span.filesize { margin-left: 5px; }

span.spoiler { background: #ddd!important; color: #ddd !important; border-bottom: 1px #aaa dotted; }
span.spoiler:hover { background: inherit !important; color: inherit !important; border: 0; }

span.commentpostername ~ a > img { margin-bottom: 3px !important; }
span.filesize ~ a > img { margin-right: 10px !important; }

a.omittedposts + a + table { margin-top: 3px !important; }

select + label.pointer { margin-left: 3px; }

hr + table td[align=left] span:first-child { text-decoration: line-through; opacity: 0.5; }
hr + table td[align=left] span:first-child + span { display: none; }

span.postername > span, span.postertrip > span, span.commentpostername > span { opacity: 1; }

a.linkmail[href="mailto:sage"], a.linkmail[href="mailto:sage"] * { color: red !important; border-bottom: 1px red dotted !important; }

div#BF_Widget, div.bf, div.postarea > form > div, div.logo ~ div > a > img, center > a > img { display: none !important; }
table#recaptcha_table td:last-child { display: none; }
table#recaptcha_table * { margin: 0 !important; padding: 0 !important; height: auto !important; }
table#recaptcha_table div#recaptcha_image > img { height: 57px !important; }
table#recaptcha_table a > img { margin-left: 5px !important; }

}
