css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.4chan.org"){
 body:before{
  content:"Too lazy to edit the front page CSS D:";
 }
}
@-moz-document domain("dis.4chan.org"){
 body{
  font:8pt Arial, Helvetica, sans-serif !important;
 }
 body.board{
  background:#ffc !important;
  color:#007 !important;
 }
 body.read{
  background:#ff0 url("http://img195.imageshack.us/img195/6236/smileyawesome16.png") fixed center no-repeat !important;
  color:#007 !important;
 }
 .head, body.board .thread{
  -moz-border-radius:5px !important;
  -webkit-border-radius:5px !important;
  background:#ff0 url("http://www.gamethreat.net/forums/images/smilies/awesome/awesome.png") fixed center no-repeat !important;
  border:2px inset #fff !important;
 }
 .postnum{
  font-size:20px !important;
 }
 .postername{
  color:#000 !important;
 }
 .postertrip{
  color:#aaa !important;
 }
 blockquote{
  font:8pt Tahoma, sans-serif !important;
 }
 .border, .hborder{
  -moz-border-radius:10px !important;
  -webkit-border-radius:10px !important;
  border:2px outset #fff !important;
  background-color:#000 !important;
 }
 h2 a:link, h2 a:visited, h2 a:active, h2 a:hover{
  color:#909 !important;
 }
 #ad div.ad-title a, .bottomAdFoot a:hover, .adText, .bottomAdTitle, .postblock{
  display:none !important;
 }
 #bottomAdOuter{
  display:none !important;
 }
 .spoiler{
  background-color:#000 !important;
  text-shadow:none !important;
 }
 .spoiler:hover{
  background-color:transparent !important;
  color:#000 !important;
 }
 .quote{
  border-left:solid 5px #f00 !important;
  padding:0 0 0 5px !important;
  margin:3px 0 !important;
 }
 .quote .quote{
  border-left:solid 4px #0f0 !important;
 }
 .quote .quote .quote{
  border-left:solid 3px #00f !important;
 }
 .quote .quote .quote .quote{
  border-left:solid 2px #f0f !important;
 }
 .quote .quote .quote .quote .quote{
  border-left:solid 1px #0ff !important;
 }
 .quote:hover{
  background-color:transparent !important;
  color:#fff !important;
  text-shadow:#000 0px 0px 5px !important;
 }
}
