css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") 
{

body {font-family:Georgia, "Times New Roman", serif !important; line-height: 1.5em !important; background:#222!important; color: #dfdfdf!important; font-size:16px!important; }






   #content {border: none !important; background:#000!important; color:#999999!important; font-size:16px !important; padding:15px !important; margin:0!important; margin-top:35px!important; }

td, div{background:transparent!important;color:#999!important;}


.MainPageBG h2{background:#333!important;}

div.dablink {background:#333!important;color:#999!important}

a {color:#777777!important; border-bottom: 1px dotted #333!important;}

a.image {border:none!important;}

a img {border:none!important;}





   #column-content{margin:0!important;}



.pBody {background-color:transparent!important; border:none!important; padding:0!important; text-transform:lowercase;}

.infobox, .vevent,#contentSub, table.navbox, .toc, .catlinks, .metadata,.plainlinks,.mbox-small, .noprint, .tright, .portal, .infobox, .geography, .vcard{-moz-border-radius:3px !important;-webkit-border-radius:5px!important; color:#999!important;background:#111111!important;}

.wikitable {margin:10px!important; min-width:100px;color:#777!important;background:#000000!important;}

.wikitable th {background:#444!important;color:#999!important;}

table.navbox {border-color:#555!important;}
table.navbox th{background:#444!important;}
table.navbox td{border-color:#333!important;}
table.navbox span{background:none!important;}


table.vertical-navbox.nowraplinks {float:right!important; clear:right!important; color:#999!important; background:#111111!important; -moz-border-radius:3px !important;-webkit-border-radius:5px!important;text-align:center!important; line-height:1.4em!important; font-size:82%!important;}



.summary {background-color:#DDD!important;color:#999!important;}

.toccolours th{background:#555!important;}


.ambox {text-align: left !important; margin:0!important; border-left:10px solid #AAA!important; -moz-border-radius:5px;-webkit-border-radius:5px; margin-bottom:10px !important;color:#999999!important;}

#p-logo, #p-interaction, #n-contents, #footer,#p-tb, #n-currentevents, #p-personal, .editsection,#siteNotice, #p-search h5,#p-navigation h5, #siteSub, #p-search input.searchButton,#mw-page-base,#ca-nstab-main,#n-featuredcontent,#searchButton {display:none!important;}

h2 {clear:left; color:#BBBBBB!important;}
h3 {clear:left; color:#BBBBBB!important;}


.cquote {color:#666!important;}

div.thumbinner {background:#111!important;border:none!important;}

.suggestions {position:absolute;right:13px!important; top:35px!important; -moz-border-radius:30px!important;width:auto!important;color:#999999!important;}

.suggestions-results {border:1px solid #000!important;padding:5px!important;min-width:180px!important;background:rgba(0,0,0,0.9)!important;color:#999999!important;}

.suggestions-result,.suggestions-special .special-query,.suggestions-special .special-label{color:#999999!important; text-align:right!important;}

.suggestions-special {-moz-border-radius:10px!important;-moz-border-radius-topleft:0!important; -moz-border-radius-topright:0!important;margin-top:0px!important;border:1px solid #000!important;padding:5px!important;border-top:none!important;width:auto!important;background:rgba(0,0,0,0.9)!important;color:#dbdbdb!important;}
.suggestions-results .suggestions-result-current {background:none!important;color:#6699CC!important;}
.suggestions-result-current,.suggestions-result-current .special-query,.suggestions-result-current .special-label  {color:#6699CC!important;}

#mw-panel.collapsible-nav div.portal {margin:0!important;}
div#mw-panel {position:static!important;width:100%!important; clear:both!important;margin:0!important;padding:0!important;padding-top:0!important;height:auto!important; top:0!important;}
#p-lang {background:none!important;width:100%!important;margin:0!important;padding:0!important;position:relative!important; bottom:0!important; left:0!important; display:block!important;margin:0!important;padding:0!important;top:-80px!important;}
#p-lang h5 {width:100%!important;border-bottom:1px solid #CCC!important; color:#dbdbdb!important;}
#p-lang .body{margin:0!important;padding:0!important;}
#p-lang .body ul {padding:10px!important;}
#p-lang ul li {float:left!important; margin-left:10px!important;}
#p-lang ul li a {color:#ccc!important;}

#p-coll-print_export{left:125px!important;background:none!important;position:absolute!important;top:5px!important; margin:0!important; padding:0!important;}
#p-coll-print_export .body {padding:0!important; margin:0!important;}
#p-coll-print_export .body ul{padding:0!important; margin:0!important;background:#fafafa!important; padding:5px!important;border:1px solid #aaa;margin-top:6px!important;border-top:none!important;left:18px;-moz-box-shadow:0 0 10px rgba(0,0,0,0.5); -moz-border-radius-bottomright:5px;-moz-border-radius-bottomleft:5px;}
#p-coll-print_export h5 {background:none!important;color:#aaa!important;text-transform:lowercase!important;padding:0!important; margin:0!important;}
#p-namespaces ul{position:absolute!important;top:-36px!important;left:-50px!important;}
div.vectorTabs .selected a span{font-weight:bold!important; color:#CCC!important;}
div#mw-head {position:static!important;display:inline !important;}
#p-navigation {position:static !important; display:inline !important;}
#p-namespaces {position:static!important;display:inline !important;}

div.vectorTabs {background:none!important;}
div.vectorTabs ul {float:none!important;background:none!important;position:absolute !important;z-index:3000!important;list-style-type:none !important; list-style-image:none !important;top:5px!important;left:0px!important; height:auto!important;}
div.vectorTabs ul li {background:transparent!important;list-style-type:none !important; list-style-image:none !important; float:left!important; margin-left:10px!important;height:auto!important;line-height:1em!important;text-transform:lowercase!important;}
div.vectorTabs ul li.selected {background:none!important;}

div.vectorTabs ul li a span {padding-top:0!important;color:#aaa!important;border-bottom:none !important;}

#p-navigation ul {position:absolute !important; top:2px !important; right:215px !important; list-style-type:none !important;list-style-image:none !important; float:left;}
#p-navigation ul li {list-style-type:none !important; list-style-image:none !important; float:left; margin-left:5px!important;}
#p-navigation ul li a {border-bottom:none !important; color: #aaa !important;}
#p-cactions li a {padding:8px !important;}
#p-search {position:static!important;}
#searchform {display:block !important; width:200px!important; position:absolute!important; top:0px!important;}
#simpleSearch {position:absolute!important; top:0px!important;}
li#ca-talk {margin-right:0 !important;}
h1#firstHeading {border:none !important; font-style:italic; font-size:2.5em !important; color:#BBBBBB!important; margin-bottom:15px !important; line-height:1em !important;padding-top:5px!important;padding-bottom:0px!important;} 
#column-content {float:none !important; width:auto!important;}
#searchBody {padding:0!important; margin:0!important; border:0 !important; background:none!important;}
#bodyContent p {line-height:1.8em !important;}

input#searchInput {position:absolute!important;top:1px!important;width:158px !important; right:5px!important;margin:0!important; font-family:Georgia, "Times New Roman", serif!important;font-size:11px!important; color:#555!important;background:#fafafa url(data:image/png;base64,) no-repeat 5px 3px!important; padding-left:22px!important;padding-right:10px!important;-moz-border-radius:30px!important;}

#simpleSearch {margin:0!important;margin-top:0!important; background:none!important; border:none!important;}
#p-cactions li {border:0 !important; background:none !important; background-color:transparent !important;}
#p-cactions li a {border:0 !important; background:none !important; background-color:transparent !important; color:#dbdbdb !important; }
#p-cactions {left: -22px !important; top: 7px !important;}
#simpleSearch label {padding:0!important;top:-3px!important; left:22px!important; font-size:11px!important; display:none!important;}
#searchInputSuggest {right:0px !important;}
.dablink {background-color:#DDD!important; color:#222!important; padding:5px 10px 
!important; -moz-border-radius:5px; margin-bottom:15px !important;}
.dablink a {}
#toc {background-color:transparent !important;}
#column-one {padding-top:0 !important;}
#contentSubs, #contentSub2s {margin:0px!important;margin-bottom:5px!important; margin-top:-10px; text-indent:0!important;font-size:10px!important;}
#contentSub, #contentSub2 {position:absolute!important; top:30px; right:15px;}
#contentSub, #contentSub a {color:#555!important;}
table {background:transparent!important;}

div.vectorTabs ul li a,li#n-mainpage-description a,li#n-randompage a{opacity:0.6;display:block;background:transparent!important;background-image:url(data:image/png;base64,)!important;height:16px!important;width:16px!important;overflow:hidden!important;text-indent:-9999px!important;padding:0!important;border-bottom:none !important;border:5px solid #111!important; }
div.vectorTabs ul li a:hover,li#n-mainpage-description a:hover,li#n-randompage a:hover {opacity:1;border-color:#999!important;background-color:#333!important;}
li#ca-view a{background-position:0px 16px!important;}
li#ca-view a:hover{background-position:0px 0px!important;}
li#ca-edit a{background-position:-80px 16px!important;}
li#ca-edit a:hover{background-position:-80px 0px!important;}
li#ca-history a{background-position:-48px 16px!important;}
li#ca-history a:hover{background-position:-48px 0px!important;}
li#ca-talk a{background-position:-16px 16px!important;display:none!important;}
li#ca-talk a:hover{background-position:-16px 0px!important;display:none!important;}
li#n-mainpage-description a{background-position:-64px 15px!important;}
li#n-mainpage-description a:hover{background-position:-64px -1px!important;}
li#n-randompage a{background-position:-112px 16px!important;}
li#n-randompage a:hover{background-position:-112px -1px!important;}
}
