/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: transparent url('http://i28.tinypic.com/2uyhzlc.jpg') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}





.new_post_label{

font-size:14px !important;

padding-top:5px !important;

font-family:sans-serif, Georgia, Times New Roman !important;

font-style:bold !important;

text-transform: lowercase !important;

color:#82878a !important;

letter-spacing:1px !important

}
.post{opacity:0.7 !important; border-bottom:0px !important; color:#373c3e}

.post:hover{opacity:0.9 !important}
