css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zv.uni-wuerzburg.de") {
	/* Links */   
	a:visited, .links2visited:visited, .links2:visited {
		background-color: transparent !important;
		text-decoration: none !important;
		color: darkblue  !important; 
	}
	a:hover, .links2visited:hover, .links2:hover {
		background-color: transparent !important;
		text-decoration: none !important;
		font-weight: bolder !important;
		color: blue  !important; 
	}
	.links3 {
		color: darkblue !important;
	}

	/* Leiste fÃ¼r Loginstatus */
	.divloginstatus {
		background-color: #eee !important;
		color: black;
		border-bottom: 2px solid black;
		margin-bottom: 0px !important;
		border-top: 2px solid black;
		margin-top: 5px;
	}
	.divloginstatus td {
		color: black !important;
	}


	/* Linkleiste */
	.divlinks {
		padding-left: 0px !important;
		background-color: #eee !important;
		border-bottom: 2px solid black;
		margin-bottom: 10px;
		border-top: 2px solid black;
		margin-top: 0px !important;
	}
	.divlinks a {
		padding-left: 4px !important;
	}
	.divlinks a:hover {
		border-left: 3px solid black !important;
		border-right: 3px solid black !important;
	}

	/* Leiste "Wo bin ich" */
	.breadcrumb {
		background-color: #ddd;
		padding: 2px 9px;
	}
	.breadcrumb > a {
		text-decoration: none !important;
		font-weight: bold;
	}

	/* Semesterwahlbox */
	#choosesemesterajax .links3 {
		color:white !important;
	}
	#choosesemesterajax .links3:hover {
		border: 0px !important;
	}

	/* Contentbereich */
	.content_max_portal, .content {
		background-color: transparent !important;
	}

	/* Navigation */
	#makronavigation {
		margin-right: 7px !important;
		background-color: #eef !important;
		padding: 0px !important;
		margin: 0px !important;
	}
  	#makronavigation .auflistung {
		display:block !important;
	}
	#makronavigation a.auflistung, #makronavigation a.visited {
		background-color: transparent;
		border: 0px solid black;
	 	padding: 3px 6px !important;
		min-height: 15px;
	 }
	#makronavigation a.auflistung:hover {
		border-left: 4px solid darkblue !important;
		border-top: 2px solid darkblue !important;
		border-bottom: 2px solid darkblue !important;
	}

	/* Hilfelink im FuÃŸ und Links im Menue oben rechts */
	.divfoot .links3, .divfoot .links3:visited, .divfoot .links3:hover, #menu_oben a:hover {
		color: white !important;
	}

	/* Hintergrundfarbe der Formularelemente entfernen */
	input:focus, input:hover, input:active, select:focus, select:hover, select:active, textarea:focus, textarea:hover, textarea:active {
		background: none !important;
	}
}
