css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {


.mi {
                        color:#ffffff!important;
}

#overhead {
	              
	               width:0px!important;
                       height:0px!important;
                       background:transparent;
}
.oh-ml oh-mls { display: none; }

#overhead:hover {
                        
	                padding:11px!important;
	                z-index:9!important;
	                width:auto!important;
	                height:auto!important;
}

#overhead a:hover { 
                        color:transparent!important; 
}

#overhead a { 
                        color:transparent!important;
}


#overhead:hover a:hover { 
                        color:#FFFFFF!important; 
}

#overhead:hover a { 
                        color:#FFFFFF!important;
}

#overhead .oh-darker { 
                        display:none!important; 
}

#overhead .oh-mg { 
                        display:none!important; 
}

#overhead .friendsmenu { 
                        display:none!important;
}

#overhead .collectmenu { 
                        display:none!important;
}


#overhead:hover .friendsmenu { 
                        display:inline!important;
}



#overhead:hover .oh-darker { 
                        display:inline!important;
}

#overhead:hover .oh-mg { 
                        display:inline!important;
}

.simple {
                        color:#333333!important;
}

.oh-hhs {
                        display:none!important; 
}


.oh-darker {
                        color:#FFFFFF!important;
}

.oh-darker:hover {
                        color:#E5E5E5!important;
}

.oh-mmain { 
                        display:none!important;
}

.damncr-title {
                        background:#BCCBBA!important;
                        color:#707070!important;
} 

.damncrc-topic {
                        background:#D2DFD1!important;
                        border:none!important;
                        color:#707070;
}

a:hover {
                        color:#333333!important;
                        text-decoration:none!important;
}
 
.damncrc-chat-window {
                        border:1px solid #BCCBBA!important;
}

.damncr-members dt { 
                   
                        background:#d2dfd1!important;
}

div.damncr-members {
                        background:#D2DFD1!important;
                        color:#707070!important;                
}

.damnc-morelink {
                        color:transparent!important;                
}

.damnc-helplink {
                        color:transparent!important;  
                        right:678px!important;              
}
#sdprefsbtn { right:690px!important; }
.inner {
                        color:#707070!important; 
}

.alt0 {
                        background:#BCCBBA!important;
}

.damnc-tabbar {
                        font-size:11px!important;    
}

.damnc-tabbar a { 
                        height:15px;
                        background:#BCCBBA!important;
                        -moz-border-radius:7px 0px 0px 0px!important;               
} 

.damnc-tabbar a i { 
                        background:#BCCBBA!important;
                        -moz-border-radius:0px 7px 0px 0px!important;
}

.damnc-tabbar strong.withX { 
                       
                        background:#BCCBBA!important;
                        -moz-border-radius:7px 0px 0px 0px!important;
                   
}

.damnc-tabbar strong.withX i {
                        background:#BCCBBA!important;
                        -moz-border-radius:0px 7px 0px 0px!important;
                        width:7px!important;
}
.damncr .self-hl .from span {
                        background:#EFEFEF!important;
                        padding:0px 5px 0px 5px!important;
                   
}

.damncr .other-hl .from span {
                        background:#FFFFFF!important;
                        padding:0px 5px 0px 5px!important;
                        -moz-border-radius:5px!important;
}

.damncr .other-hl .text span {
                        background:#FFFFFF!important;
                        padding:0px 5px 0px 5px!important;
                        -moz-border-radius:5px!important;
}

.damncrc-icon-roomname {
                        display:none!important;
}

.damncrc-iconbar a {
                        color:#707070!important;             
}

.damncrc-iconbar a:hover {
                        color:#FFFFFF!important;    
                        text-decoration:none!important;         
}

.damncrc-iconbar {
                         color:transparent!important;
}

.superdamn-buttonbar {
                         position:relative!important;
                         top:0px!important;
}

input {
                         -moz-border-radius:.4em!important;
}
}
