css:
toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #urlbar {
  -moz-border-radius: 3px 0 0 3px !important;
  border-right: 0 !important;
}

toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #reload-button,
toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #stop-button{
  border: 1px solid transparent !important;
  -moz-border-radius: 0 3px 3px 0 !important;  
  -moz-box-shadow: 0 1px rgba(255, 255, 255, 0.2) !important;
  background: -moz-linear-gradient(#D6D6D6, #D6D6D6 1px, #F7F7F7 1px, #F7F7F7 2px, #FFF 2px, #FFF),
              -moz-linear-gradient(rgba(0,0,0,.5), rgba(0,0,0,.2)) !important;
  margin-left: -4px !important;
  list-style-image: url("data:image/png;base64,") !important;
  max-width: 23px !important;
  min-width: 23px !important;
  -moz-transition: -moz-box-shadow .1s !important;
  background-origin: padding-box, border-box !important;
  background-clip: padding-box, border-box !important;
}

toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #reload-button:-moz-window-inactive,
toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #stop-button:-moz-window-inactive  {
  background-image: -moz-linear-gradient(#D6D6D6, #D6D6D6 1px, #F7F7F7 1px, #F7F7F7 2px, #FFF 2px, #FFF),
                    -moz-linear-gradient(rgba(146, 146, 146, 0.84), rgba(146, 146, 146, 0.84)) !important;
}

toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #reload-button {
  -moz-image-region: rect(0 11px 12px 0) !important;
}

toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #stop-button {
  -moz-image-region: rect(0 22px 12px 11px) !important;
}

toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #reload-button:not([disabled]):hover{
  border: 1px solid transparent !important;
  background: -moz-linear-gradient(rgb(158,204,241), rgb(111,177,225) 49%,
                                   rgb(91,159,217) 50%, rgb(81,152,210) 60%,
                                   rgb(62,138,204)),
              -moz-linear-gradient(rgba(0,0,0,.5), rgba(0,0,0,.2)) !important;
  background-origin: padding-box, border-box !important;
  background-clip: padding-box, border-box !important;
  -moz-box-shadow: 0 1px rgba(255, 255, 255, 0.2),
                   0 1px 0 0 rgba(0,0,0,.1) inset,
                   0 0 0 1px rgba(255, 255, 255, 0.1) inset !important;
}

toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #stop-button{
  background: -moz-linear-gradient(rgb(230,161,138), rgb(209,119,100) 49%,
                                   rgb(192,93,78) 50%, rgb(185,85,70) 60%,
                                   rgb(193,90,74)),
              -moz-linear-gradient(rgba(0,0,0,.5), rgba(0,0,0,.2)) !important;
  background-origin: padding-box, border-box !important;
  background-clip: padding-box, border-box !important;
  -moz-box-shadow: 0 1px rgba(255, 255, 255, 0.2),
                   0 1px 0 0 rgba(0,0,0,.1) inset,
                   0 0 0 1px rgba(255, 255, 255, 0.1) inset !important;
}

toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #reload-button:not([disabled]):hover:active,
toolbar[mode="icons"][currentset*="urlbar-container,reload-button,stop-button"] #stop-button:not([disabled]):hover:active {
  -moz-box-shadow: inset rgba(0, 0, 0, 0.3) 0 -6px 14px, 
                   inset #000 0 1px 4px, inset rgba(0, 0, 0, 0.2) 0 1px 4px,
                   0 1px rgba(255, 255, 255, 0.2)!important;
}

#stop-button .toolbarbutton-icon,
#reload-button .toolbarbutton-icon{
  height: auto !important;
  width: auto !important;
}
