  color: #fff !important;
  text-decoration: underline !important;
}

#navigation .current-page { 
  background: #7392c1 !important; 
  color: #f9e917 !important;
  text-decoration: none !important;
}

a:link, #sidebar h2 a { color: #26457d !important; }
a:visited { color: #7f7d73 !important; }
li a:hover { background: #e7e4d1 !important; }
#account-link a { color: #fff !important; }

#search-terms {
  background-color: #fff !important;
  border: 1px solid #6382b1 !important;
}

#search input[type="submit"] {
  border: 1px solid #82a5da !important;
  background-color: #7392c1 !important;
  color: #fff !important;
}

/* no google ads (sorry, np, but it doesn't display properly since I'm styling the html tag or something) */
