css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("my.neumont.edu") {

td.portlet_header
{
   background-color: #494949 !important;
   color: #EEEEEE !important;
}

td.portlet_content b.niftycorners
{
   display: none !important;
}

td.portlet_header b.niftycorners
{
   background-color: #333333 !important;
}

b.rb1, b.rb2, b.rb3, b.rb4, b.rb5, b.rb6, b.rb7, b.rb8
{
   background-color: #333333 !important;
   border-style: none !important;
   border-color: #333333 !important;
}

b.r1, b.r2, b.r3, b.r4
{
   background-color: #494949 !important;
   border-style: none !important;
   border-color: #333333 !important;
}

body
{
   background-color: #333333 !important;
   color: #CCCCCC !important;
}

.page_content
{
   background-color: #333333 !important;
}

.portlet_content, blockquote
{
   background-color: #3A3A3A !important;
   color: #CCCCCC !important;
}

a:link
{
   color: #308FC9 !important;
}

a:hover
{
   color: #EEEEEE !important;
}

td.portlet_content td, blockquote td, ul td
{
   background-color: #404040!important;
   color: #C0C0C0 !important;
   border-style: none;
}

td.portlet_content th, blockquote th, ul th
{
   background-color: #3D3D3D !important;
   color: #C0C0C0 !important;
}
td.portlet_content, blockquote
{
   border-style: solid !important;
   border-width: 1px !important;
   border-color: #494949 !important;
   background-color: #444444 !important ;
}

.regular_tab
{
   background-color: #404040 !important;
}

.regular_tab a:hover
{
   color: #CCCCCC !important;
}

.active_tab
{
   background-color: #555555 !important;
   color: #F0F0F0 !important;
}

div img
{
   display: block !important;
}

blockquote th
{
   color: #EEEEEE !important;
}

h3, h4
{
  color: #CCCCCC !important;
}

h1, h2
{
  color: #3F9AD1 !important;
}

blockquote
{
   margin: 10px 10px 10px 10px !important;
   padding: 10px 10px 10px 10px !important;
}

.floatingBox
{
   border-color: #308FC9 !important;
}

p
{
   border-style: none;
}
}

@-moz-document url-prefix(https://my.neumont.edu/threaded-bboard/one-message) {
table td
{
   background-color: #404040 !important;
   color: #C0C0C0 !important;
   border-style: none !important;
}

table th
{
   background-color: #3D3D3D !important;
   color: #C0C0C0 !important;
}

table
{
   border-style: none !important;
}

.regular_tab a:hover
{
   color: #CCCCCC !important;
}

.regular_tab
{
   padding: 5px !important;
}

.tab_spacer
{
   background-color: #333333 !important;
}

.active_tab
{
   background-color: #555555 !important;
   color: #F0F0F0 !important;
}
}
