@namespace url(http://www.w3.org/1999/xhtml);

/* Mozilla.zine Dark CharcoalBlue */
/* Xavian-Anderson Macpherson */
/* http://userstyles.org/users/3665 */
/* Shingoshi@Gmail.net */
/* NASA Night Launch Specific Modifications */
/* https://addons.mozilla.org/en-US/firefox/addon/4908 */

@-moz-document domain("mozillazine.org"),
               domain("mozilla.com"),
               domain("mozdev.org"),
               domain("mozilla.org"),
               domain("xulplanet.com"),
               url-prefix(http://www.google.com/custom),
               url-prefix(http://kb.mozillazine.org/),
               url-prefix(http://forums.mozillazine.org/),
               url-prefix(http://forums.mozillazine.org/)
{
/*=========================================*/
/* Starting here */


/*=========================================*/
/* Assorted Link Handling */
a:hover, a:active
 {
 color: #0a0 !important
 }
a:visited
 {
 color: red !important
 }
a:visited:hover
 {
 color: #10b !important;
 font-weight: bolder !important
 }
/* End of the Link */
/*=========================================*/


/*=========================================*/
/* Kill the Carpet Baggers */
[class="gas"],
[class="th gas_head"],
[class="gas_side"]
 {

 display: none !important;
 }
/*=========================================*/


/*=========================================*/
/* Headings */
h1, h2, h3, h4, h5, h6,
td.row1, td.row2, td.row3,
p, dl, multicol, td
 {
 color: #aaa !important ;
 background-color: #000 !important
 }

[id="masthead"] {display: block !important;}
td.row3Right,
td.spaceRow
 {
 background-color: black !important;
 border: solid 1px #FFF !important;
 color: white !important;
 }

th.thHead,th.thSides,th.thTop,th.thLeft,th.thRight,th.thBottom,th.thCornerL,th.thCornerR
 {
 color: #ddd !important;
 font-size: 12pt !important;
 font-weight: bold !important;
 border: #fff 1px !important;
 border-style: solid !important;
 height: 22px !important;
 }

/*.th, th
 {
 height: 100% !important; 
 }*/

.th, th
 {
 color: #ddd !important;
 font-size: 12px !important;
 font-weight : bold !important;
 background: none repeat-x bottom center !important;
 border: #fff 1px !important;
 border-style: solid !important;
 height: 22px !important;
 margin: 0px 0px 0px 0px !important;
 }
/*=========================================*/


/*=========================================*/
/* This is where the specification for all lines and most borders are given. */

[class="sidebar"],
[class="sidebar searchBox"]
 {
background: #050518 !important;
border: solid 1px white !important;
 }

table img
 {
 border-left-color: #000 !important;
 border-right-color: #000 !important;
 border-top-color: #000 !important;
 border-bottom-color: #000 !important;
 }

[class="rowpic"]
 {
 background: #005 none repeat-x bottom center !important;
 height: 26px !important;
 }

td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom
 {
  background: none repeat-x bottom center !important;
  border: #000 !important;
  border-style: none !important;
  height: 24px !important;
}
/*=========================================*/


/*=========================================*/
/* Black Text */
ul.os_linux a.download-link strong,
ul.os_osx a.download-link strong,
ul.os_windows a.download-link strong,
[class="download-link download-firefox"],
[class="install-green-button"]
 {
 color: #000 !important;
 font-weight: bold !important;
 }
/*=========================================*/


/*=========================================*/
/* BlueSky text */
#nav li span
 {
 color: #8bf !important;
 }
/*=========================================*/


/*=========================================*/
/* Blue Slate text */
[id="content"],
[id="mainContent"],
[id="side"]
 {
 color: #679 !important
 }
/*=========================================*/


/*=========================================*/
/* Backgrounds */
/* BlueSky Backgrounds */
table img
 {
 background-color: #8bf !important;
 }
/*=========================================*/


/*=========================================*/
/* BlueSlate Backgrounds */
#header ul a,
[class="selected new"] #header li,
[class="selected istalk"] #header li
 {
 background-color: #679 !important;
 }
/*=========================================*/


/*=========================================*/
/* BlueCarbon Backgrounds */
checkbox,
#nav,
#nav li span,
[class="app_install"],
[id="nav"]
 {
 background-color: #002 !important;
 }
/*=========================================*/


/*=========================================*/
/* Black Background */
/* Black Background */
body,
.box, .box div,
#breadcrumbs,
#catlinks,
#container,
.corner-box,
#footer,
html,
.key-point,
.menu-box,
.menu-box ul,
.menu-box ul li,
#nav, #nav li a, #nav ul,
#page-title,
.sidebar div,
table,

[class="addon-feature-install"],
[class="bz_comment"],
[class="ch"],
[class="content"],
[class="date"],
[class="indented"],
[class="indented selected"],
[class="newsitem"],
[class="ns-0"],
[class="post"],
[class="product-description"],
[class="t bb"],
[class="variation1"],

[id="aubg"],
[id="banner"],
[id="content"],
[id="firefox"],
[id="front-feature"],
[id="mainContent"],
[id="mBody"],
[id="nav"],
[id="q"],
[id="res"],
[id="side"]
 {
 background-color: #000 !important;
 background-image: none !important;
 }
/*=========================================*/


/*=========================================*/
[id="sidebar"],
[id="main"],
[class="text"]
 {
 background: #000 !important;
 }
/*=========================================*/


/*=========================================*/
/* LightGray Text */
[class="cattitle"],
[class="gen"],
[class="genmed"],
[class="gensmall"],
[class="liteoption"],
[class="mainoption"],
[class="row1"],
[class="row2"]
 {
 color: #aaa !important;
 }
/*=========================================*/


/*=========================================*/
body, select,
[class="anonymous-div"] {
    color: #fff !important;
background: #000 !important;
}
/*=========================================*/


/*=========================================*/
[class="postbody"],
.postbody
 {
 background-color: #000 !important;
 font-weight: bold !important;
 font-size : 10px !important;
 color: #bbbbbb !important;
 line-height: 12px !important;
 background: #000 !important
 }
/*=========================================*/


/*=========================================*/
[class="postsig"]
.postsig, postsig span
 {
 font-weight: normal; color: #bbb !important
 }

[class="quote"]
.quote
 {
 background-color: #141414 !important;
 font-size: 10px; color: #ddd !important
 }
/*=========================================*/


/*=========================================*/
[class="code"]
.code
 {
 font-weight: normal; color:
 #0ff !important;
 background-color: #181818 !important
 }
/*=========================================*/


/*=========================================*/
[id="main"]
[id="sidebar"]
 {
 background: #000 !important;
 }
/*=========================================*/


/*=========================================*/
/* This is where the specification for all lines and most borders are given. */
.forumline
 {
 background-color: #005 !important;
 border: 1px #005 solid !important;
 }


/*=========================================*/
td.row3t,td.spaceRow
 {
 background-color: black !important; border: #FFF !important; border-style: solid !important;
 }
/*=========================================*/


/*=========================================*/
a
 {
 color: #80b1ff !important;
 text-decoration: none !important;
 }

/*=========================================*/
.poll
 {
 font-weight: bold !important
 }
/*=========================================*/


/*=========================================*/
.sticky
 {
 font-weight: bold !important;
 color: #80b1ff !important
 }
/*=========================================*/


/*=========================================*/
.announce
 {
 font-weight: bold !important;
 color: #900 !important
 }
/*=========================================*/


/*=========================================*/
.moved
 {
 font-weight: bold !important;
 color: #999 !important
 }
/*=========================================*/


/*=========================================*/
[class="mainoption"],
[class="liteoption"],
[type="submit"]
 {
 -moz-appearance: none !important;
 margin: 0px 4px !important;
 border: 0px !important;
 color: #ccc;
 height: 24px;
 padding-left: 10px !important;
 text-align: center !important;
 font-weight: bold !important;
 list-style-position: inside !important;background: none !important; 
 }
/*=========================================*/


/*=========================================*/
[class="sidebar"],
[class="sidebar searchBox"]
 {
 background: #050518 !important;
 border: solid 1px #fff !important;
 }
/*=========================================*/


/*=========================================*/
table img,
[class="divider"]
 {
 background-color: #000 !important;
 background-image: none !important;
 border-left-color: #111 !important;
 border-right-color: #111 !important;
 border-top-color: #111 !important;
 border-bottom-color: #111 !important;
 }
/*=========================================*/

/* Ends here */
}
