@-moz-document domain(userstyles.org),
               url-prefix("http://userstyles.org/") {

/* DarkUserStyles */
html, body
 {
 background: none !important;
 color: gainsboro !important;
 background-color: black !important;
 }

/* Black Borders */
[id="frmPostDiscussion"]
 {
 color: #aaa !important;
 background-color: #111 !important;
 border-top-color: #111 !important;
 border-left-color: #111 !important;
 border-bottom-color: #111 !important;
 border-right-color: #111 !important;
 }

a:link { color: cornflowerblue !important }
a:hover, a:active { color: #0a0 !important }
a:visited { color: red !important }
a:visited:hover { color: #ff3 !important; }


/* Gray Fonts */
BUTTON
 {
 font-weight: normal;
 color: #ccc !important;
 background-color: #005 !important;
 border-color: dimgray !important
 }

#search-terms,
ul#navigation a,
input[type="submit"]
 {
 font-weight: normal;
 color: #aaa !important;
 background-color: #000 !important;
 border-color: dimgray !important
 }

input#search-terms { background-color: black !important }
pre { background-color: #1a1a1a !important; border: 1px solid darkgray !important }

#Header ul li a,
#header-bar
 {
 background-color: #005 !important
 }

.post-your-style
 {
	clear: both;
 }

/* userstyles.org Forum */
/* White Fonts */
input, textarea,
[class="CommentBody"],
#Header h1 { color: #fff !important; }

/* Black Backgrounds */
textarea, input,
#AboutVanilla, #Discussions li ul, #Panel
 {
 background-color: #000 !important;
 }

[class="CategorySelect"],
[class="SearchInput"],
[id="CommentBox"],
[id="CommentBoxController"],
[id="frmPostComment"],
[id="Header"], body,
[id="Panel"],
[id="SearchSimple"]
 {
 background-color: #000 !important;
 background-image: none !important;
 }

/* Graphical header link */
  #logo {
    background-image: none !important;
    margin: 0 !important;
    padding: 0 !important;
  }

  #logo > a {
    display: block !important;
    width: 268px !important;
    height: 64px !important;
    text-indent: -700em !important;
    background-image: url("data:image/png;base64,") !important;
    background-position: top left !important;
    background-repeat: no-repeat !important;
  }

