@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ubuntuforums.org") {

body {background-image:none !important; background-color:#3E3E3E !important;}

.tborder {border:0px !important;}

span.forumhome_stats {border:none !important;}

.page, .vbclean_topfg, .vbclean_top *, .vbclean_bottom *, .alt1, .alt2, .tborder, .alt1Active, .pagebody, .tcat, .vbmenu_control, .ubuntu_quotebackground, .panelsurround, .panel, .vbclean_bottomfg, .tfoot, .forumhome_stats, .vbclean_postbitmsgfg, .vbclean_postbitmsg *, .vBulletin_editor {background-color: #606060 !important;}
.vbclean_bottom *, .vbclean_top *, .vbclean_postbitmsg * {border-left: 1px solid #606060 !important; border-right: 1px solid #606060 !important;}

.alt1 {border-bottom:0 !important; border-right: 0 !important;}
.vbclean_postbitmsgfg .tborder tbody td {border-right:0 !important;}

.alt3 {background-color: #525252 !important;}

.vbclean_postbitlegfg, .vbclean_postbitleg *, .alt4 {background-color:#505050 !important;}
.vbclean_postbitleg * {border-right:1px solid #505050 !important; border-left:1px solid #505050 !important;}

.vbclean_forumdescfg, .vbclean_forumdesc * {background-color:#323554 !important;}
.vbclean_forumdesc * {border-right: 1px solid #323554 !important; border-left: 1px solid #323554 !important;}

.vbclean_postbitlegfg .alt4 {border-bottom:2px solid #3E3E3E !important;}
.vbclean_postbitlegfg .alt3 {border-right:2px solid #3E3E3E !important;}
.forumhome_stats {border: 1px solid #2D2D2D !important;}

.vbclean_navbar_bg, .vblcean_navbar *, .vbclean_newthreadfg, .vbclean_newthread * {background-color:#3E3E3E !important;}
.vblcean_navbar *, .vbclean_newthread *  {border-right: 1px solid #3E3E3E !important;border-left: 1px solid #3E3E3E !important;}

.tcat {border-bottom: 1px solid #4A4A4A !important;}

.thead, .vbclean_fhomecatfg, .vbclean_fhomecat *, .vbclean_fhomecatfg .forumhome_stats {background-color: #525252 !important;} 
.vbclean_fhomecat * {border-right: 1px solid #525252 !important;border-left: 1px solid #525252 !important;}

.ubuntu_quotebackground {border:solid #3E3E3E !important; border-width: 1px 1px 1px 7px !important;}

.thead {border-bottom:2px solid #404040 !important;}

.vbclean_fhomecat_topfg, .vbclean_fhomecat_top *, .vbclean_fhomecat_topfg .forumhome_stats {background-color:#434750 !important;}
.vbclean_fhomecat_top * {border-right:1px solid #434750 !important; border-left:1px solid #434750 !important;}

#navbar, .pda, .posttop {background-color:#4e4e4e !important;}
.posttext {background-color:#5F5F5F !important;}
.post {border:1px solid #4e4e4e !important;}

/**TEXT**/
* {color:#B5B5B5 !important;}
.smallfont span, *:link {color:#6090A9 !important;}

/**IMAGES**/
img[src*="images/misc/ubuntulogo.png"] {width:0px !important; height:55px !important; background:url(data:image/png;base64,) no-repeat !important; padding-right:202px !important; z-index:1 !important;}

}
