css:
@-moz-document url-prefix(http://lifehacker.com) {
div.postText, div.post-full {
  padding-right: 0px !important;
  width: 850px !important;
  font-size: 15px !important;
}
  
div.comment, #commenterPage .postDetails .mainTag, #commenterPage .postDetails .postText {
  width:850px !important;
}

hr.separator, #comments .comment p, #CommentsWrapper, #CommentListWrapper {
  width: 850px !important;
}

div.comment-tools, div.post-tools, #sidebar, *[id^="ad"], #skyscraper, #button, #jobs, hr.spacer, *[id$="Ad"], #sponsors {display: none !important;}

#content {width: auto !important;}
}
