css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gigazine.net") {

body * {
     font-size: 12px !important;
     line-height : 1.5em !important;
}

html , body  { 
     background: #555555 none !important;
     margin: 0px !important;
     padding: 0px !important;
}

div {
     background: none #FFFFFF !important;
     border: none !important;
}

ul {
     padding: 0px 0px 0px 10px !important;
}

#leftcol li {
    display: block !important;
    margin: 0px 0px 5px 0px !important;
    padding: 0px 0px 5px 0px !important;
    border-bottom: 1px dotted #AAAAAA !important;
}


#compagewidth ,
#pagewidth {
     width: 900px !important;
     padding: 0 10px 0px 10px !important;
     margin: 0 auto !important;
}

#header {
     border: none !important;
}

#header br {
     display: none !important; 
}

#maincol {
     width: 45% !important;
     min-width: 400px !important;
     padding: 0% 3%  0% 3% !important;
}

.sbn {
     width: 400px !important;
}

#maincol img {
    max-width: 400px !important;
}

#maincol iframe {
    width: 400px !important;
    height: 245px !important;
}
#leftcol {
     width: 24% !important;
     margin-left: 0px !important; 
}

#rightcol {
     width: 25% !important;
     margin-right: 0px !important;
}

div.jobboard_blog_parts , 
#rightcol script + script + iframe + h3 + form + iframe + script + h3 {
     display: none !important;
}

.title {
     width: 370px !important;
     font-size: 16px !important;
     background: #FFFFFF !important;
     padding: 3px 10px 10px 10px !important;
     border-bottom: 1px solid #FEDC01 !important;
     border-left: 10px solid #FEDC01 !important;
     height: auto !important;
}

h3.sidetitle {
    padding: 0px 0px 0px 1em !important;  
}

a.mytitle , h2.title {
     font-size: 16px !important;
}

/*広告削除
広告以外も消していたらすみません*/
*[id*="google_ads"] ,
#headbn ,
#headerbtm ,
#leftcol iframe ,
#rightcol iframe ,
#gad_script_ef ,
#outer iframe[src*="INSERT_RANDOM_NUMBER_HERE"] ,
#maincol .content iframe:first-child ,
#maincol .content iframe:first-child + br ,
#maincol .content iframe:first-child + br + br ,
#maincol .content ins:last-child {
    display: none !important;
}

#grid-content iframe {
    display: block !important;
}

script + br ,
script + br + br {
    display : none !important;
}

#header img[src*="logo"] {
     width: 75% !important;
     height: 75% !important;
     display: inline !important;
}

table.calendarBG td,
table.calendarBG td a,
table.calendarBG th {
    font-size: 10px !important;
    padding: 1px !important;
    
}

}
