css:
@-moz-document domain("facebook.com") {



  #ad_0, #ad_1, #ad_2, #ad_3, #ad_4, #ad_5, #ad_6, #ad_7, #ad_8, #ad_9, #ssponsor, .advert, .ad_body,
  .ad_capsule, .ad_media, .comments_ad_image, .sponsors, .sponsor_absolute, #new_stuff_content,
  .nextstep, div#ssponsor, #sidebar .divider_bar {
    display: none !important;
  }

  #sidebar a.go_home {
    background: url(http://img216.imageshack.us/img216/5853/facebooknyloggape5fc5.gif) !important;
  }

  #sidebar a.go_home:hover {
    background-position: 0px 55px !important;
  }

  #navigator {
    background: url("http://img405.imageshack.us/img405/9117/facebookvakgrundio1.gif") #3B5998 !important;
  }

  #navigator .main_set li a.edit_link { color: #BDBDBD !important; }
  #navigator .main_set li a.active, #navigator .main_set li a:hover { background: #4A4A4A !important; }

  #navigator .main_set li a.global_menu_arrow, #navigator .main_set li a.global_menu_arrow_active {
    background: url("http://img442.imageshack.us/img442/391/globalmenuarrowjb3.gif") !important;
  }

  #navigator .main_set li a.global_menu_arrow:hover, #navigator .main_set li a.global_menu_arrow_active:hover {
    background-position: 17px 0px !important;
  }

  #navigator .secondary_set li a { color: #C7C7C7 !important; }

  #app_non_nav_list { margin-top: -10px !important; }
  .social_ad * { margin: -5px !important; }
  h2 a { color: #4C618D !important; }

 
  #navigator .navigator_menu { border:1px solid #000000; }

 
  #expandable_more { display: block!important;}
  #more_section { display: none!important; }


}
