css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("thepiratebay.org") {



a:link, a, #categoriesTable, a:active {color:#ff6600 !important; border:0 !important;, text-decoration:none; text-shadow: #000000 3px 3px 7px; text-decoration: underline !important; }
a:hover {color: #aa4400; text-decoration: none !important; }


h1, h3, #topleft, #topright, .spons-link, #sky-right, #kopimi, a[href*="/settings"], a[href*="/language"], #fbanners, #smalltext, #footer, #foot, #open-software {display:none !important}

div.ad, .ad, .ads, iframe, #TPBlogo, spons-link, a[href*="adclick"], img[src*="http://static.thepiratebay.org/img/bar.gif"] {display: none !important; }

#q  { text-align:center !important; }

body { background: #222222; font-family:"Verdana"; align:right; color:#dd6600; text-shadow: #000000 3px 3px 7px !important; }

p{font-family:"Times New Roman", Times, serif;}

#categoriesTable 
 
a:link, a:active, a:visited {color: #dd6600; text-decoration: underline !important; }
a:hover {color: #aa4400; text-decoration: none !important; }

h2  { background: #333333; font-family:"Verdana"; border-width:1px; border-color:black; align:right; color:#dd6600; shadow: #000000 3px 3px 7px !important; }

#tableHead {background: #333333 !important; }

.searchbox, #searchfield {display: none !important; }

#details { background: #333333; shadow: #000 3px 3px 7px !important; }

#desc, .nfo, .detailsFrame { background-color : #222; border: 1px solid #ff6600; !important; }

#details, #artistDetails  {color: #ff6600 !important; }

#comments div { padding:1px 5px; margin:1px 0 ; background-color:222!important }

#comments .comment { background-color : #222; color: #777; border: 1px solid #ff6600; !important; }

#comments p.byline { background-color : #333; color: #777; font-size:12px;!important; }

table#searchResult th { background:#333 !important; border: 0 !important; }
table#searchResult td { background:#111 !important; width: 5px; border: 1px solid #884422; border-right: 1px solid #884422 !important; }
table#searchResult a:link { color: #ff6600!important; }

input, #category { background:#2d2d2d; color:#ff6600; text-shadow: #000000 3px 3px 5px; border:1px solid #aa4400; !important;}

#detailsframe #title, #err, #details dt, #details dd, #detailsframe { background:#333; color:#ff66oo; text-shadow: #000000 3px 3px 7px; border:0px solid #aa4400 !important;}

tbody td {width: 72px !important; }

#irc { background:#222; text-shadow: #000000 3px 3px 7px; border: 1px #FFBD32; !important;}

#about, #policy { width:inherit !important}

#msg { background-color : #333; border: 1px solid #ff6600; !important; }

}
