css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pcinpact.com") {

#home_zone{width:280px !important;}

#pub_bandeau, #bandeau,#pub1, #pub2, #top_pdn,#pdn,#news_iv
{display:none !important;}
.news_source>*,
.news_source>div:first-child>div:last-child,
.txt_marge>div
{display:none;}

.corps_news,.news_source
{display:inline !important;}

.entete_news,
.news_source>div:first-child,
.news_source>.txt_marge_news,
.news_source>.news_actus,
#sondage > .txt_marge > div
{display:block !important;}

.news_source>div:last-child{display:none !important;}

#bloc_droite > .titre_bloc_moyen_sondage {display:none;}
#test_iv{border-bottom: 1px solid #999999 !important;}

/* Modif position lien premium */
#bloc_droite{position:relative;}
#link_to_premium{position:absolute !important;left:0;bottom:-80px;}
#breves{border-bottom:1px solid #999999;}



/* modif hover custopack */
a img[alt="Custo pack, par Crystal XP"]{border:0 none !important;border-left:4px solid #6199F0 !important;}
a img[alt="Custo pack, par Crystal XP"]:hover{border-left-color:#E2641B !important;}


/* custo menu */
.back,#fond_search{background-image:url('http://i20.servimg.com/u/f20/12/00/18/36/fond-b10.png') !important;}
.back:hover,#fond_search:hover{background-position:bottom left !important;}

/* custo images articles */
.test_centre .dos_line_img a{
display:block;
}
.test_centre .dos_line_img a:hover{
border:1px solid #E2641B;
border-width:1px 10px 1px 3px;
overflow:hidden;

border-top-color:#828282;
-moz-border-right-colors:transparent transparent transparent transparent transparent transparent transparent #828282 #E2641B #E2641B;
border-bottom-color:#828282;
-moz-border-left-colors:#828282 #E2641B #E2641B;
}

.test_centre .dos_line_img a:hover img{
margin:-1px -10px -1px -3px;
}


}
