css:
@-moz-document domain('boards.4chan.org') {
body { background: #2C2C2C !important; color: #eee !important; }

        input { text-shadow: none !important;}

	* { font-family: "Helvetica Neue", Arial, sans-serif !important; text-shadow: #000 0px 1px 1px; }

	a { color: #efefef !important; }

	hr { background-color: #2c2c2c; border-color: #2C2C2C; !important; }

	#navtop, #navbot { background: #000; !important; width: 100%; text-align: center; height: auto; font-size: 10pt !important; border-bottom: solid #fff 1px; padding-bottom: 5px; text-shadow: none !important; font-family: "Courier Regular" !important; letter-spacing: 1px; }

#thread_filter {
       background: #2C2C2C !important; color: #eee !important; }
#watcher {
       background: #2C2C2C !important; color: #eee !important; }
#updater {
       background: #2C2C2C !important; color: #eee !important; }
#navbot { border-top: solid #fff 1px; }

	#navtop *, #navbot * { color: #eee; font-size: 7pt; display: inline; }

	#navtop a:hover, #navbot a:hover { background: #555; }

	.inputtext, textarea { width: 295px; border: 1px #666 dashed; margin: 5px; }

	td.postblock { background: #444 !important; border: 1px dashed #666 !important; color: #fff !important; font-size: 12px; }

 	td.reply, td.replyhl { background-color:#333 !important; border: 1px solid #555 !important; color: #eee !important;-webkit-border-radius: 8px; -webkit-box-shadow: #000 0px 0px 3px; -moz-border-radius: 8px; -moz-box-shadow: #000 0px 0px 3px;}

	td.replyhl { background-color:#4B4B4B !important; border: 1px dashed #4B4B4B !important;	color: #eee !important; }

	.postername, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, .replytitle, .commentpostername, .omittedposts, .abbr { color: #fff !important; font-size: 8pt;}

	span.filetitle, .replytitle { color: #e15014 !important; }

	#hd, small, #ft, #filter-button, #option-button, #navtopr, #navbotr, .logo,.logo font b span, .doubledash, img.rotating, input[type="button"], td[align="right"] { color: #f33e3e; border: none; font-size: 14px; }
.logo font b span {
font-size: 30px !important; }

.logo { margin-top: 15px;}

	.pages { background: #444 !important; border: 1px solid #555 !important; }

	.pages td { color: #444 !important; font-size: 4pt; }

	.pages td a, .pages td b { color: #eee !important; font-size: 8pt; }

	.pages td b { background-color: #000; }

	.pages input { display: none !important; }

	.pages td:first-child { display: none;}

	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select { font-size: 12pt ! important; color: #fff !important; background: #333 !important; border-color: gray !important; }

	input[type="submit"] { background: #444 !important; color: #fff !important; border: 1px dashed gray;  font-size: 12pt; }

	th { background: #222 !important; }

	th font { font-size: 7pt; }
input.button {text-shadow: none;)
	td.deletebuttons { font-size: 8pt !important; color:#eee !important; }

}
}
