css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("audionews.org") {

.string1:hover, .string2:hover {
    background-color: #FFE668 !important;
}

.bordbt {
line-height:140%;
padding:5px !important;
}
.bordbt a {
font-size: 1em !important;
}
form table .bordbt a:visited, a.genmed:visited{
color: #000 !important;
border-bottom: 1px solid red;
}

td.genverysmall a{
font-size: 0.8em !important;
}

td.menu {
height:22px !important;
background-position: 0px -15px !important;
}

#toolbar {
padding: 5px 10px !important;
height: auto!important;
}


textarea, input, select, option, optgroup, label, fieldset {
font-size: 12px;
}
.fldSetCont1 select[multiple="multiple"] {
height: 270px;
}
.fldSetCont1 select[name="sns"] {
height: 23px;
display: block;
width: 113px;
}
label[for="search_method_and"], label[for="search_method_or"] {
height: 13px; display: block;
}

.input_butt, .liteoption {
font-size: 11px !important;
}

.sidebar { display: none !important; }

div[style="position: absolute; top: 80px; right: 560px;"] {
top:3px !important;
right:20px !important;
}

#toTop {
    right: 15px !important; 
    bottom: 10px !important;
}

}
