css:
#urlbar{text-align:center!important; margin: -2px -1px -1px !important}

.tabbrowser-strip{height: 18px !important}
.tabs-bottom{display:none!important}

.tabbrowser-tab{
  font-weight:normal!important;
  text-align:center!important;
  margin: -2px -1px 4px !important;
  padding: 0 6px 1px !important;
  border-right: 1px solid!important;
  border-left: 1px solid!important;
  border-bottom: 1px solid!important;
  -moz-border-bottom-colors: ThreeDShadow !important;
  -moz-border-right-colors: ThreeDShadow !important;
  -moz-border-radius-bottomleft: 12px !important;
  -moz-border-radius-bottomright: 12px !important}
.tabbrowser-tab:not([selected="true"]):hover{
  margin: -3px 0px 4px !important;
  padding-top: 2px !important;
  -moz-border-radius-bottomleft: 12px !important;
  -moz-border-radius-bottomright: 12px !important}
.tabbrowser-tab[selected="true"]{
  margin: -3px 0px 4px !important;
  padding-top: 2px !important;
  -moz-border-radius-bottomleft: 12px !important;
  -moz-border-radius-bottomright: 12px !important}

.tabbrowser-tab .tab-text{border:none!important}
