css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.com.pk"),domain("www.google.com"),domain("images.google.com.pk") ,domain("images.google.com")  {

#gfx-header{
   padding-left:0 !important;
}
table#sft tbody tr td table[style*="margin-top: 25px;"]{
   margin: 40px 0 0 !important
}
#res .g, #mfr {
   margin:0 0 14px 8px !important; 
   max-width:100% !important; 
   min-width:660px !important; 
   min-height:76px !important; 
   padding:15px 20px 15px 8px !important; 
   -moz-border-radius:2px !important;
}
#res .g:hover, #mfr:hover {
   border-color:#789BDC !important;
}
ol li.g{
   border-left-width:55px !important; 
}
li.g span.cnt{
   color:#fff !important; 
   margin-left:-64px !important; 
   width:55px !important; 
   text-align:center !important;
   font-family:"Myriad Pro" !important; 
   font-size:30px !important;
}
div#ssb{ 
   margin-bottom:10px !important; 
}
a:hover{
   text-shadow:none !important
}
#res .g a.l{
   font-family:"Myriad Pro" !important; 
   font-size:20px !important;
}
#cnt{ padding-top:10px !important;}
#gfx-head-content #logo img{ display:block !important;}
#gfx-head-content{
   margin-top:23px !important;
   padding-top:13px !important;
   padding-bottom:5px !important;
}
div.rshdr{ display:none !important;}
#gbar_bg, #gfx-preview .menus{
   background-color: #D3E1F9 !important;
   background-image: none !important;
}
#cnt{ padding:0 !important;}
#gbar{ left:6px !important;}
input[type="text"]:focus, input[name="q"]:focus{
   outline:1px none #7D7D7D !important;
}
input.lst[title="Search"]{
   -moz-border-radius-topleft: 12px !important;
   -moz-border-radius-bottomleft: 12px !important;
   -moz-box-shadow:0 1px 2px rgba(0, 0, 0, 0.1) !important;
}
input.lsb[type="submit"]{
-moz-appearance: none !important; 
   -moz-border-radius:0 12px 12px 0 !important;
   -moz-box-shadow:0 1px 2px rgba(0, 0, 0, 0.2) !important;
   cursor:pointer !important;
   display:block !important;
   font:14px/100% Arial,Helvetica,sans-serif !important;
   margin:0px 2px 0 !important;
   outline:medium none !important;
   padding:5px 10px 7px !important;
   text-align:center !important;
   text-decoration:none !important;
   text-shadow:0 1px 1px rgba(0, 0, 0, 0.3) !important;
   vertical-align:baseline !important;
   border:1px solid #0076A3 !important;
   color:#D9EEF7 !important;
   background: -moz-linear-gradient(center top , #0095CC, #00678E) repeat scroll 0 0 transparent !important;
}
input.lsb[type="submit"]:hover{
   background: -moz-linear-gradient(center top , #00ADEE, #0078A5) repeat scroll 0 0 transparent !important;
}
input.lsb[type="submit"]:active{
   background: -moz-linear-gradient(center top , #0078A5, #00ADEE) repeat scroll 0 0 transparent !important;
   color:#80BED6 !important;
   position:relative !important;
   top:1px !important;
}
span.ds input.lsb[type="submit"]{
   -moz-border-radius:12px !important;
}
#sfcnt div.lsd{margin:6px 0 0 !important;}
div#leftnav{
   position: fixed !important;
   padding-top: 112px !important;
   overflow: hidden !important;
}
}
