css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("urasoku.blog106.fc2.com") {

div#BlogName ,
div#headIntroduction {
    display: none !important;
}

div#container {
     overflow: hidden !important;
}

div#container > br {
     display: none !important;
}

div#container , 
div#headBlock1 ,
div#headBlock2 ,
div#wrapper ,
div#footBlock {
     width: 900px !important;
}

div#headBlock1 ,
div#headBlock2 ,
div#wrapper ,
div#footBlock {
     margin: 0px !important;
     padding: 0px !important;
}

div#headBlock1 {
     height: 200px !important;
}

div#headBlock2 {
     height: 20px !important;
}

div#main-column {
     width: 670px !important;
     padding: 0px 20px 0px 0px !important;
}

div.CTBody {
     width: 94% !important;
     padding: 0% 3% !important;
}

div.mainEntrytop {
    width: 660px !important;
    overflow: hidden !important;
}
div#right-column , 
div.topbox , 
iframe[width="720"] , 
iframe[width="570"] ,
iframe[src*="brainer.jp"] ,
div.LinkBlock ,
div#main-column > center ,
div.googleAdContainer {
     display: none !important;
}

strong {
     line-height: 1.2em !important;
}

div.EntryBlock {
     overflow: hidden !important;
}

div.EntryBlock ol {
     margin: 0px !important;
     padding: 0px !important;
}

div.EntryBlock li {
     margin-left: 30px !important;
}

}


@-moz-document domain("hamusoku.com") {
div#container {
    width: 960px !important;
}

div.column-inner {
    margin-right: 0px !important;
}

div#extra ,
div.topbox ,
div.column-inner-2 > center ,
div.article-outer-3 > center ,
div.google-user-ad ,
div.blog-title-outer-3 > a[href="http://hamusoku.com/"] + a ,
#footer_custom {
    display: none !important;
}

}
