css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.aujourdhuilejapon.com")  {

/*
  display: none !important;
  background-color: #fff !important;
  background-image: none !important;
  border: 2px dashed #9d020a !important;
  -moz-border-radius: 10px ! important;
*/

* {
  background: #fff !important;
}

body, html {
  margin-bottom: 50% !important;
}

a:link, a:visited {
  text-decoration: none !important;
  border-bottom: 1px dashed #ccc !important;
}
a:hover, a:active {
  border-bottom: 1px solid #aaa !important;
}

#support #menu , /* menu haut */
#zonecomplement , /* menu droite */
#menu-gauche ,
#menu-bas,
.hrgris,
#zonearticle-large p a strong , /* poster un commentaire */
#support div p i , /* liens commerciaux (#1) */
#zonearticle-large p a.suite , /* tous les ... */
div .publiciteleft ,
.signature ,
#support #publicitebas,
.icone
{ display: none !important; }

#support #contenu #zonearticle p i /* ANNULE (#1) ci-dessus , pour le texte des videos */
{ display: inline !important; }

#zonearticle {
  position: absolute !important;
  top: 0px !important;
  left: 6% !important;
  width: 60% !important;
  max-width: 550px !important;
}
#zonearticle h1,
#zonearticle h2 {
  font-size: 14px !important;
}

#zonephoto , img { -moz-opacity: 0.27; }
#zonephoto:hover , img:hover { -moz-opacity: 1; }

}
