css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*** Tab Bar ***/

.tabbrowser-tabs {
height: 23px !important;
}
#TabsToolbar {
background-color: transparent !important;
background-image: url("data:image/png;base64,") !important;
background-repeat: repeat-x !important;
margin-bottom: -1px;
}
#TabsToolbar:-moz-window-inactive {
background: url("data:image/png;base64,") !important;
}
#TabsToolbar * {
background-color: transparent !important;
}

/*** Tab ***/

.tabbrowser-tab {
margin-bottom: -1px !important;
height: 24px !important;
border-left: 11px !important;
border-right: 11px !important;
border-bottom: 2px !important;
-moz-border-image: url("data:image/png;base64,") 0 11 2 11 repeat stretch !important;
}
/* Unselected (inactive) */
.tabbrowser-tab:not([selected="true"]):-moz-window-inactive {
-moz-border-image: url("data:image/png;base64,") 0 11 2 11 repeat stretch !important;
}
/* Selected (inactive) */
.tabbrowser-tab[selected="true"]:-moz-window-inactive {
-moz-border-image: url("data:image/png;base64,") 0 11 2 11 repeat stretch !important;
background-color: transparent /*-moz-mac-chrome-inactive*/ !important;}
/* Selected (active) */
.tabbrowser-tab[selected="true"] {
-moz-user-focus: normal;
-moz-border-image: url("data:image/png;base64,") 0 11 2 11 repeat stretch !important;
height: 22px;
margin: 0px;
padding:0px 1px 0px 0px !important;
background-color: transparent !important;
}

/*** Tab Ordering ***/

/* Left of selected */
.tabbrowser-tab:not([selected="true"]) {
border-right: 0 !important;
padding-right: 12px !important;
}
/* Leftmost */
.tabbrowser-tab:first-child:not([selected="true"]) {
border-left: 0 !important;
padding-left: 11px !important;

}
/* Right of selected */
.tabbrowser-tab[selected="true"] ~ .tabbrowser-tab {
border-left: 0 !important;
border-right: 11px !important;
padding-left: 11px !important;
padding-right: 1px !important;}
.tabbrowser-tab:not([selected="true"]):hover {
background: transparent !important;
background-repeat: repeat-y !important;
}

/*** Close Button ***/

/* Close (selected) */
.tabbrowser-tab[selected="true"] .tab-close-button {
list-style-image: url("data:image/png;base64,") !important;
}
/* Close (unselected, hover) */
.tabbrowser-tab .tab-close-button:hover {
list-style-image: url("data:image/png;base64,") !important;
}

/* Close (unselected) & Close (selected, hover)*/
.tabbrowser-tab .tab-close-button,
.tabbrowser-tab[selected="true"] .tab-close-button:hover {
margin-top: 1px !important;
list-style-image: url("data:image/png;base64,") !important;
}
/* Close (unselected, active) */
.tab-close-button:hover:active {
list-style-image: url("data:image/png;base64,") !important;
}

/* Close (selected, active) */
.tabbrowser-tab[selected="true"] .tab-close-button:hover:active {
list-style-image: url("data:image/png;base64,") !important;
}


/*** New Tab Button ***/

.tabs-newtab-button {
-moz-box-flex: 1;
-moz-box-pack: end !important;
margin: 1px 0px -2px 0px !important;
padding: 0px 2px 0px 1px !important;
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: rect(4px 7px 3px 7px) !important;
}
.tabs-newtab-button:hover,
#TabsToolbar #new-tab-button:hover {
margin-right: 1px !important;
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: rect(4px 7px 3px 7px) !important;
}
.tabs-newtab-button:hover:active,
#TabsToolbar #new-tab-button:hover:active {
list-style-image: url("data:image/png;base64,") !important;
}
.tabs-newtab-button:-moz-window-inactive,
#TabsToolbar #new-tab-button:-moz-window-inactive {
list-style-image: url("data:image/png;base64,") !important;
}
#TabsToolbar #new-tab-button {
width: 30px !important;
padding: 0px !important;
margin: -1px 0px -2px 0px !important;
background: none !important;
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: rect(6px 7px 5px 7px) !important;
background-color: transparent !important;
}

/*** Scroll Arrows ***/

/* Right Arrow */
.tabbrowser-arrowscrollbox > .scrollbutton-down {
border: none !important;
}
