css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  url-prefix(http://www.itmedia.co.jp/news/articles/)
 ,url-prefix(http://www.itmedia.co.jp/anchordesk/articles/)
{

/* Clean Header Space */
 div#COIDP
,div.header
,div#txtad
,div#btnlist
 {
   display: none !important; 
 }

/* Clean Side Space */
 div.rc
 {
   display: none !important; 
 }

/* Clean footer Space */
 div.mediafooter
,div#globalFooter
,div#newzia_connect_flnews
,div.adsense
,div.bookmarks
,div.tbshow
,div#itsb
 {
   display: none !important; 
 }

/* Centering Contents */
 div.l600
 {
   margin       : 0px auto  !important;
   padding      : 10px 15px !important;
   border-left  : solid 1px #DDD !important;
   border-right : solid 1px #DDD !important;
   width        : 80% !important;
   max-width    : 1200px !important;
 }

}
