css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imdb.com") {

/** October 7, 2010 changes **/
#swf_07{
padding-top: 25px !important;
}
.bannertop, .twentyanniversary{
margin: auto !important;
}
#sidebar{
position: absolute !important;
left: 0px !important;
width: 25% !important;
}

#content-2-wide #main{
position: relative !important;
left: 26% !important;
width: 70% !important;
}

.redesign .article{
background: -moz-linear-gradient(top, #FFFFFF, #D8D4A5 5px, #BAAE59) !important;
}
.article{
border: 2px white solid !important;
-moz-border-radius: 12px 12px 12px 12px !important;
padding: 10px 12px !important;
}
.rating-big .rating-bg, .rating-big .rating-imdb, .rating-big .rating-cancel, .rating-big .rating-stars .rating-your, .rating-big .rating-stars .rating-hover, .rating-big .rating-error-icon{
background-color: transparent !important;
}
.links, #content-1, #content-2-wide, .rating-stars a, .rating{
background-color: transparent !important;
}
.star-box{
background: -moz-linear-gradient(top left , #D8D4A5, #BAAE59) repeat scroll 0 0 !important;
}
#footer{

background: -moz-linear-gradient(top, #BAAE59, #9E923A 100px, #8F5919) repeat scroll 0 0 !important;
}
/** End Oct 7 2010 specific changes **/

.article embed, #video-player-container, div#video-player, embed#player{
display: inline !important;
visibility: visible !important;
}

body, #wrapper{
background: -moz-linear-gradient(top, #C9C181, #8F5919) !important;
margin: 0px !important;
padding: 0px !important;
width: 100% !important;
}
#nb20{
position: relative !important;
top: 0px !important;
}
#root{
position: relative !important;
top: -25px !important;
}
#wrapper{
position: relative !important;
top: 0px !important;
background-image: none !important;
margin: 0 !important;
padding: 0 !important;
width: 100% !important;
}
#pagecontent, #root{
background-color: transparent !important;
margin: 0 !important;
}
#pagecontent{
height: 1400px !important;
}

#root {
margin: auto !important;
background: -moz-linear-gradient(top, #FFFFFF, #EEE69E 115px,#E0D894 150px, #CAC282 200px, #BAAE59)!important;
background-position: top left !important;
margin: 0 !important;
width: 100% !important;
}
table#outerbody{
width: 100% !important;
}


/* THE WHOLE OCT 3 2009 UPDATE #####################################*/
#nb20{
background: #FFFFCD url(http://www.imdb.com/images/nb15/sprocket.gif) repeat-x scroll !important;
}
#navbar {
background-color: #666666 !important;
background: url(http://www.imdb.com/images/nb15/searchbg.gif) repeat-x scroll center bottom !important;
width: 100% !important;
border-bottom: none !important;
}
#navbar a#home_img{
top: -5px !important;
height:60px !important;
}
.hpRightNav{
background: #D5D2A2 !important;
border: none !important;
}
#nb_search{
top: 0px !important;
color: #65530E !important;
}
#nb_personal{
top: 19px !important;
}
/* ############################################################## */

#tn15lhs{
/*border: 1px solid #847955 !important;*/
}
td.lhscol{
/*border: 2px solid #93803f !important;*/
border-top: none !important;
border-right: #D1CD97 !important;
padding-top: 20px !important;
padding-bottom: 20px !important;
background-color: #d5d2a2 !important;
}

/*div.hpleftnav div.boxbottom,*/
td[bgcolor='#6677dd']
{
background-color: transparent !important;
}
td[bgcolor='#6677dd'] span font
{
color: #655300 !important;
}
#bottom_ad_wrapper,
#floating1_wrapper,
#top_ad_wrapper,
.sponsored_links,
#news-disclaimer,
.bottom-only{
  display: none !important;
}

/* HIDE RULER ON VOTING BARS */
td[background="http://i.imdb.com/ruler.gif"]{
background-image: none !important;
}

                    /*  -- SIDE BOXES BORDERS - */
.hpLeftNav div.boxBottom, 
.hpRightNav div.boxBottom{
background-color: transparent!important;
}
.boxSides{
background-color: transparent!important;
}

.hpLeftNav div.boxSides, .hpRightNav div.boxSides{
border-color: #847955!important;
}

td[width="14"][bgcolor="#6677dd"], tr[bgcolor="#6677dd"]{
background-color: transparent !important;
}
td[width="3"][bgcolor="#6677dd"]{
background-color: #87773b !important;
}


td[width="30"][valign="top"][height="28"][bgcolor="#ffffff"][align="left"]{
background-color: transparent !important;
}

                       /* -- Front Page -- */
#nb15 {padding-top: 0px !important;
margin: 0 !important;
}

table{
background-color: transparent !important;
}


div.photo {
background-color: #d5d2a2 !important;
}


            /* ----------- Movie Page ----------- */
            /* ----------- Title --------------- */
#tn15title span{
color: black !important;}


.strip
{background-color: transparent !important;
border: none !important;}

.even, .chart_even_row, td[class="movie even"], td[class="comingsoon even"]
{background-color: #E5DDA7 !important;} /* -- dark --- */

.odd, .chart_odd_row, td[class="movie odd"], td[class="comingsoon odd"]
{background-color: #E8E5CF !important;} /* -- light  -- */



                        /*  -  lefthand menus - */
div.hpLeftNav, #tn15lhs {
background-color: #d5d2a2 !important;
}

                    /* - Top & Bottom Movie List -- */
table table table tr[bgcolor="#e5e5e5"]{
background-color: #E5DDA7 !important;
}
table table table tr[bgcolor="#ffffff"]{
background-color: #E8E5CF !important;
}


                /* ------- Photos ------- */
td[width="35%"][valign="top"][bgcolor="#dde0e9"][align="center"], 
td[width="15%"][valign="top"][bgcolor="#dde0e9"][align="left"]{
background-color: #E5DDA7 !important;
}
td[width="35%"][valign="top"][bgcolor="#ffffff"][align="center"], 
td[width="15%"][valign="top"][bgcolor="#ffffff"][align="left"]
{
background-color: #E8E5CF !important;
}
td[width="15%"][valign="top"][bgcolor="#dde0e9"][align="left"],
td[width="15%"][valign="top"][bgcolor="#ffffff"][align="left"]{
padding: 15px !important;
}
td[width="15%"][valign="top"][bgcolor="#ffffff"][align="left"] table tr td[colspan="3"]
{
display: none !important;
}
div.resume_box, td.nconst_url{
background-color: transparent !important;
border: none !important;
}



                         /* ---- LINKS -- --  */
a:link {color: #8F802D !important;}
a:visited {color: #504d0d !important;}


div a img {
color: tan !important;
}

div.main{
background-color: transparent !important;
border: none !important;
}

              
                     /* ----- Movie Page ----- */

#tn15main
{border: none !important;}

.photo{
border: none !important;
}

#boxoffice, #moviemeter{
border: none !important;
}

                /* -- Forum Stuff - */
/*table[width="100%"][cellspacing="0"][cellpadding="5"]*/ td[bgcolor="#006699"],
/* -- My Movies --*/ table table table tr [bgcolor="#9999ff"]{
background-color: /*#7D7416*/#978443 !important;
background-image: 
url(http://pokerhermit.googlepages.com/eyebrowcontainerbrown2.png) !important;
background-repeat: repeat-x !important;
}
td[width="90%"] font[size="+1"][face="Arial, Helvetica, sans-serif"][color="#000000"]{
color: #ffffff !important;
}

table td[bgcolor="#dfdfdf"]{
background-color: #d3d09d !important;
}
td[width="20"][height="28"]{ /* Skinny left column of search results */
background-color: transparent !important;
}
#tn15main table.boards{
margin-top: 20px !important;
}

#tn15main table.boards th, .chart_head {
background-image:
url(http://pokerhermit.googlepages.com/eyebrowcontainerbrown2.png)
!important;
}

/* NEW PHOTO GALLERY Feb 24 */
#media_canvas{
background-color: transparent !important;
border: 3px #847955 solid !important;
color: black !important;
}


img[src="http://i.media-imdb.com/images/SF840d04cd416fd1883b6a0ff2682dc4cc/media_icon_see_all.gif"],
img[src="http://i.media-imdb.com/images/SFc19d3fb7d1149bdfa44466034489e1c5/media_icon_slideshow.gif"],
img[src="http://i.media-imdb.com/images/media_icon_slideshow.gif?c19d3fb7d1149bdfa44466034489e1c5"],
img[src="http://i.media-imdb.com/images/media_icon_see_all.gif?840d04cd416fd1883b6a0ff2682dc4cc"]{
display: none !important;
}

#media_canvas table#principal td img{
border: 5px #6F6136 solid !important;
}
.nominees, .nominees>h2>span{
background-color: #e3e1c0 !important;
}
/*Related Galleries*/
.aux-content-widget-2{
background: transparent !important;
border: none !important;
}
#footer{background-color: green !important;}
}
