css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* FIX MARGINS AND PADDINGS (MENU BUTTON HAS BEEN RESIZED) */
#main-window[sizemode="normal"] #navigator-toolbox[tabsontop="true"] #TabsToolbar  {
	margin-top: 7px !important;
}

#main-window[sizemode="maximized"] #navigator-toolbox[tabsontop="true"] #TabsToolbar  {
	margin-top: 4px !important;
	padding-left: 4px !important;
}

/* FIX CLASSIC MENU POSITION */

#main-window[sizemode="normal"] #toolbar-menubar  {
	margin-left: 98px !important;
}

/* HOVERED BUTTON APPEARANCE */

#appmenu-button-container {
	margin-left: 3px;
	background-image: url(data:image/png;base64,) !important;
	background-repeat: no-repeat;
	width: 70px !important;
	border: 1px solid rgba(255,255,255,0.5) !important;
	border-bottom-left-radius: 4px !important;
	border-bottom-right-radius: 4px !important;
}

/* NOT HOVERED BUTTON APPEARANCE */

#appmenu-button {
	background-image: url(data:image/png;base64,) !important;

	border: 0px !important;
	height: 24px !important;
	margin: 0 0 0 -3px !important;
	-moz-transition: opacity 0.3s ease-in-out !important;
	opacity: 1.0;
}

/* VARIOUS POLISHES */

#appmenu-button-container {
	padding-left: 3px !important;
	background-position: 0px 0 !important;
}

#appmenu-button:hover, #appmenu-button dropmarker  {
	opacity: 0.0;
}

#appmenu-button .button-text {
	display: none;
}
