css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.aobonline.net") {
  /* color scheme based on somethingawful.com */

  /* hide signatures */
  .genmed {
    display: none !important;
  }

  * {
    font: 13px Verdana, Arial, Sans-serif !important;
  }

  body, table, div, ul, li {
    background: #ffffff !important;
  }

  a {
    color: #003366 !important;
    text-decoration: underline !important;
  }

  table {
    border-collapse: collapse !important;
  }

  th {
    background: #006699 !important;
    color: #ffffff !important;
    border: 1px solid #aaaaaa !important;
    font-size: 11px !important;
  }

  select {
    background: #ffffff !important;
    color: #000000 !important;
  }

  .bodyline {
    background: #ffffff !important;
    color: #000000 !important;
    border: 0px !important;
  }

  .forumline {
    border: 1px solid #aaaaaa !important;
  }

  .topbkg {
    display: none !important;
  }

  .topnav, .topnav a {
    background: #777777 !important;
    border: 0px !important;
    color: #ffffff !important;
    font-size: 10px !important;
    font-weight: bold !important;
  }

  td.cat, td.cat td {
    font-weight: bold !important;
    background: #006699 !important;
    color: #ffffff !important;
    font-size: 11px !important;
    margin: 0px !important;
    padding: 0px !important;
    height: 14px !important;
  }

  a.topictitle {
    font-weight: normal !important;
  }

  a.cattitle {
    font-size: 8px !important;
    font-weight: bold !important;
    color: #ffffff !important;
  }

  .rowpic {
    background: #006699 !important;
  }

  .rowpic img {
    display: none !important;
  }

  .spacerow {
    display: none !important;
  }

  .row1 {
    border: 1px solid #aaaaaa !important;
  }

  .row1, .row1 .postbody, .row1 .genmed, .row1 .postdetails, .row1 table tbody tr td {
    background: #f1f1f1 !important;
  }

  .row2 {
    border: 1px solid #aaaaaa !important;
  }

  .row2, .helpline, .row2 .postbody, .row2 .genmed, .row2 .postdetails , .row2 table tbody tr td{
    background: #f7f7f7 !important;
  }

  .row3 {
    border: 1px solid #aaaaaa !important;
  }

  .row3, .row3 .postbody, .row3 .genmed, .row3 .postdetails, .row3 table tbody tr td {
    background: #f1f1f1 !important;
  }

  td.quote {
    color: #000000 !important;
  }

  td.spacerow {
    background: #f1f1f1 !important;
  }

  .postdetails {
    font-size: 10px !important;
  }

  input.post, textarea.post {
    color: #000000 !important;
  }

  input.mainoption, input.catbutton {
    background: #fcfcfc !important;
    color: #000000 !important;
    border: 1px solid #aaaaaa !important;
  }

  td.nav {
    font-size: 10px !important;
  }

  td.nav b {
    font-weight: bold !important;
  }

  td.nav a {
    font-weight: bold !important;
  }
}
