/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/*
By banthaz
Version 1.0 (Jun 03 2011)
*/


/* Remove OS look & Borders */
scrollbar,
scrollbar thumb
{
-moz-appearance: none !important;
border: none !important;
}


/* Colors */
scrollbar,
scrollcorner
{
background: white !important; /* Scrollbar background */
}
scrollbar thumb
{
background: grey !important; /* Scrollbar thumb */
}


/* Opacity until hover */
scrollbar:not(:hover),
scrollcorner
{
opacity: .7 !important;
}


/* Size */
scrollbar[orient="vertical"]
{
min-width: 8px !important; /* Vertical scrollbar */
}
scrollbar[orient="horizontal"]
{
min-height: 8px !important; /* Horizontal scrollbar */
}


/* Remove scroll arrows */
scrollbar scrollbarbutton
{
visibility: collapse !important;
}
