@namespace url("http://www.w3.org/1999/xhtml");

/* to make it appear in the 'google.com' sub-category of userstyles.org */
@-moz-document domain("google.com") { }


/* GENERAL STYLES ******************************/

@-moz-document url-prefix("http://www.google"),              url-prefix("https://www.google"),
               url-prefix("http://images.google"),           url-prefix("https://images.google"),
               url-prefix("http://maps.google"),             url-prefix("https://maps.google"),
               url-prefix("http://news.google"),             url-prefix("https://news.google"),
               url-prefix("http://groups.google"),           url-prefix("https://groups.google"),
               url-prefix("http://books.google"),            url-prefix("https://books.google"),
               url-prefix("http://scholar.google"),          url-prefix("https://scholar.google"),
               url-prefix("http://mail.google"),             url-prefix("https://mail.google"),
               url-prefix("http://blogsearch.google"),       url-prefix("https://blogsearch.google"),
               url-prefix("http://translate.google"),        url-prefix("https://translate.google"),
               url-prefix("http://www.google.com/calendar"), url-prefix("https://www.google.com/calendar"),
               url-prefix("http://picasaweb.google"),        url-prefix("https://picasaweb.google"),
               url-prefix("http://video.google"),            url-prefix("https://video.google"),
               url-prefix("http://docs.google"),             url-prefix("https://docs.google"),
               url-prefix("http://sites.google"),            url-prefix("https://sites.google"),
               url-prefix("http://finance.google"),          url-prefix("https://finance.google") {

#leftnavc, #fll, #bfl, #cpf, #footer, #botstuff, #resultStats, #taw, #gbe, #gbh, .gb3 { display: none !important; }


}


