css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

	#recommended_users * {
		margin: 0 !important;
		padding: 0 !important;
	}
	
	
	
	#recommended_users {
		/* display: none; */
		margin:0 !important;
		padding: 5px !important;
		position: absolute;
		top: 55px;
		right: -160px;
		opacity: 0.5;
		background: rgba(0, 0, 0, 0.5);
		-moz-border-radius: 5px;
	}
	
	#recommended_users:hover {
		opacity: 1;
	}


	#recommended_users h2 {
		margin-bottom: 10px !important;
		font-size: 10px !important;
	}

	#recommended_users h2,
	#recommended_users p {
		color: #ccc;
	}

	#recommended_users a {
		color: #15afe2!important;
	}

	#recommended_users .verified a {
		padding-right: 18px !important;
		color: #00f !important;
	}

	.next-suggestion {
		position: absolute;
		top: 0;
		right: 0;
	}

	#recommended_users li {
		margin-bottom: 10px !important;
	}

	#recommended_users .bio {
		float: none !important;
		margin-left: 40px !important;
		width: 100px !important;
		position: relative;
	}

	#recommended_users hr { display: none !important; }

	#primary_nav {
		margin-top: 20px !important;
	}
}
