css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forums.somethingawful.com"),domain("archives.somethingawful.com"),domain("forum.somethingawful.com") {

body {
    background-color: #000 !important;
    background-image: none !important;
    color: #ddd !important;
}

/* _________________________________________________  headers
   _________________________________________________ */


td.title, td.icon, td.replies, td.rating, td.posts, td.banned, #pm td, #filter, .altcolor1 td, #main_full td, div.standard, #usercpnav li, #ac_timemachine,  .searchform, .searchform h2, #forum td, #system, div.resultopts {
    background-color: #323232 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

td.author,  td.icon2, td.views, td.lastpost, td.moderators, td.users, td.archived, td.topics, .altcolor2 td, table.standard td, .result td.weight, .result td.replycount, dl.features, ul.contactlist {
    background-color: #393939 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

th.icon, th.icon2, th.title, th.replies, th.rate, th.author, th.rating, th.lastpost, th.views, .category, #pm th, #info th, th.moderators, .forumbar, .threadbar, #main_full th, .standard h2, table.standard th, #forum th, .results_table th, div.resultopts h4, .searchform h2 {
    background-color: #434343 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

/* _________________________________________________  text
   _________________________________________________ */

a               { color: #4F94CD !important; }
.drill_link     { color: #4F94CD !important; }
a:hover         { text-decoration: none !important; }
dt.author, h4   { color: #698B69 !important; }
dd.title        { color: #ccc !important; }
dd.registered   { color: #ccc !important; }
p.editedby span { color: #ccc !important; }
.author.op      { color: #4A708B !important; }
.qb2 h4         { color: #000 !important; }
.moderators     { text-align: right !important; }
#system h1      { color: #ddd !important; }
#uf_opts label  { color: #000 !important; }
.ac_results li  { color: #000 !important; }
.ac_over        { background-color: #ddd !important; }
dl.features dt.enabled { color: #00ff00 !important; }
dl.features dd.enabled h5 { color: #ddd !important; }
dl.features dd.disabled h5 { color: #ddd !important; }
dl.features dt.disabled { color: #ff0000 !important; }

#writingarea textarea {
	background-color: #4A708B !important;
	border: 1px solid #000 !important;
	padding: 0px !important;
	color: #000 !important;
} 

#forum_filters {
	color: black !important;
}

#mods, div.pages, #forum td.lastpost, #forum span.pages, #thread div.postbar, td.postdate, td.postlinks, #thread dl.userinfo dd, #thread tr.postbar td, div.threadrate, div.subscribe, div#ropt_groupby.option_col, div.resultopts .option_col {
    color: #ddd !important;
}

/* _________________________________________________  [code]
   _________________________________________________ */

pre { 
    background-color: #262626 !important;
    border: 1px solid #000 !important;
    color: #fff !important;
}

pre hr { 
    color: #333 !important;
    background-color: #333 !important;
}

img.timg {visibility:visible !important; border: none !important; } /* fix for images not showing up in quotes */


/* _________________________________________________  forum drop-down
   _________________________________________________ */

select {
    background-color: #323232 !important;
    border: 1px solid #434343 !important;
    color: black !important;
}

option {
    background-color: white !important;
    border: 1px solid #000 !important;
}

#mp_bar {
    background-color: #393939 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

#globalmenu {
    background-color: #333 !important;
}

/* ad removal */

#ad_banner_user, .ad_banner_oma, #searchboxes, #nav_purchase, #copyright, #notregistered, #unregskyscraper, div#flag_container { display: none !important; }

div.oma_pal {

display: none !important; 
}

span.bbc-spoiler { color: #000000; background-color: #000; } /* fix for spoiled text */

/* _________________________________________________  search
   _________________________________________________ */

div.search_right {
    background-color: #393939 !important;
    border: 1px solid #434343 !important;
    color: #ddd !important;
}

div.search_right option { 
    background-color: #4A708B !important;
    border: 1px solid #000 !important;
}



}
