css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ign.com") {

.share-btn-lnks, #hot-box, #commentsColCenter, #footer, #connectBox, #connBoxObjectTitle { display: none !important; }
#container-page {width: 100% !important;}
.container-content-main {width: 96% !important;}
#main-article-content {width: 100% !important;}
#content-main {width: 62% !important;}
.imageInline {padding-left: 19% !important;}
.ui-page-list ul {width: 100% !important;}
}


@-moz-document url("http://www.ign.com/"),
url("http://pc.ign.com/"),
url("http://ps3.ign.com/"),
url("http://wii.ign.com/"),
url("http://xbox360.ign.com/"),
url("http://ds.ign.com/"),
url("http://psp.ign.com/"),
url("http://tv.ign.com/"),
url("http://comics.ign.com/"),
url("http://guides.ign.com/"),
url("http://cheats.ign.com/") {

#content-main {width: 75.4% !important;}
#content-sub {float: left !important;}
#all-news {width: 100% !important;}
#top-stories {padding-left: 11% !important;}
}
