css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mbank.com.pl") 
{
 body,#bkg,#logonBlock 
 {
   background-color: #9FB5D4 !important;
 }

 body#logonPage div#logonBlock
 {
   margin-top: 4px !important;
 }

 #bkg 
 { 
   background-color: #0058D4 !important;
 }

 ul.table
 {
   border: 1px solid black !important;
   border-width: 1px 1px 1px 1px !important;
 }

 ul.table li
 {
   background-color: #FFE8C6 !important;
   border-color: #000 !important;
 }

 ul.table li.header
 {
  color: #000;
  background-color: #D49D59 !important;
 }

 fieldset
 {
  background-color: #D49D59 !important;
  border-color: #000 !important;
  color: #000 !important;
  border: 1px solid black !important;
  border-width: 1px 1px 0px 1px !important;
 }

 div > fieldset:last-of-type
 {
   border-bottom: 1px solid black !important;
 } 

 ul.table li.alternate 
 {
   background-color: #FFD9A0 !important;
 }

 fieldset.footer, ul li.footer
 {
   background-color: #D49D59 !important;
 }

 fieldset.footer
 {
   border: 1px solid black !important;
   border-width: 0 1px 0 1px;
   border-top: 0 !important; 
 }

 fieldset + fieldset.footer
 { 
   border-top: 1px solid black !important;
 }

 fieldset.offers /* Karty */
 {
   border-top: 0 !important;
   border-bottom: 1px solid black !important;
 }

 .Account a,.Actions a, .Card a, .InvestmentsType a, .ApplicationName a 
 {
   text-decoration: none !important;
 } 

 a, a:link, a:visited, body
 {
   color: #000 !important;
 }

 li.selected a
 { 
   color: #f00 !important;
 }

 div#menu li.selected 
 {
   background-image: url("data:image/gif;base64,R0lGODlhCAAIAKIAAAAAAP/////U4vcAUv///wAAAAAAAAAAACH5BAEAAAQALAAAAAAIAAgAAAMQOENK3s3JOeF6K1KWL/9OAgA7") !important;
 }

 div#menu ul.dynamic
 {
   border-width: 1px 0 1px 0 !important;
   border-color: black !important;
   padding-left: 12px !important;
 }

 li p.Actions
 {
   border-color: #8C5606 !important;
 }
 
 /* top bar */
 #banner h1
 {
   display: none !important;
 }

 #pageHeader div.col
 {
   height: 40px !important;
   background-image: none !important;
 }
 
 #logo img
 {
   margin-top: 0 !important;
 }

 #pageHeader 
 { 
   height: 45px !important;
 }

 /* forms */
 input.button, button 
 {
   -moz-appearance:none !important; 
   background-color: #ddd !important;
   border: 1px solid #000 !important;
   padding: 1px 20px !important;
 }

 input[type="text"], input[type="password"], select, textarea
 {
   -moz-appearance:none !important; 
   background-color: #eee !important;
   border-right: 1px solid #fff;
   border-bottom: 1px solid #fff;
   border-top: 1px solid #ccc;
   border-left: 1px solid #ccc;
 }
}
