css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.ogame.onet.pl/) {
	body {
		background-color: #000000;
		background-image: none;
	}
	div#main {
		position: static !important;
		background-image: none !important;
		margin: 0 auto;
		width: 886px;
		height: 0px !important;
	}
	div#onet {
		display: none;
	}
	div#copyright {
		display: none;
	}
	div.products {
		display: none;
	}
	div#login {
		position: absolute !important;
		left: 50px !important;
		top: 50px !important;
		width: 450px;
		height: 125px;
		background-image: none;
		z-index:10;
	}
	div#mainmenu {
		display: none;
	}
	div#downmenu {
		display: none;
	}
	
	.rightmenu_big {
		display: none;
	}
	.rightmenu_register {
		display: none;
	}
	.rightmenu {
		display: none;
	}
}
