@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("donbaleh.com") {

.comment{
	border:1px #c6c6c6 solid !important;
	margin-bottom: 10px !important;
	line-height: 20px !important;
}
.comment-top{
	border-bottom:1px #c6c6c6 solid !important;
	overflow:hidden !important;
	background:url(data:image/gif;base64,R0lGODlhAQAZALMAAAAAAP///+33/u/4/uz3/vH5/uLz/e34/u74/vD5/u/5/v///wAAAAAAAAAAAAAAACH5BAEAAAsALAAAAAABABkAAAQNsKSpxkD4HEGI+WAYAQA7) repeat-x !important;

}
.comment-bd{
	background:url(data:image/gif;base64,R0lGODlhAQAKALMAAAAAAP////j8//H5/vD5/vP6/vb7/vL6/vX7/vn9//T7/vf8/v///wAAAAAAAAAAACH5BAEAAAwALAAAAAABAAoAAAQIkIxTFDJLpAgAOw==) repeat-x !important;
	padding: 5px !important;
	font-size:11px !important;
}
.cmnt_agree{
	background: url(data:image/gif;base64,) left top !important;
}
.cmnt_agree:hover{
	background: url(data:image/gif;base64,) left bottom !important;
}
.cmnt_point{
	background: #fff !important;
	text-align: center !important;
	font-size: 11px !important;
	direction: ltr !important;
	vertical-align: middle !important;
	border-right: 1px #c6c6c6 solid !important;
}
.cmnt_disagree{
	background: url(data:image/gif;base64,) -26px top !important;
}
.cmnt_disagree:hover{
	background: url(data:image/gif;base64,) -26px bottom !important;
}
.cmnt_quote{
	background: url(data:image/gif;base64,) right top !important;
}
.cmnt_quote:hover{
	background: url(data:image/gif;base64,) right bottom !important;
}
.cmnt_del{
	background: url(data:image/gif;base64,) -78px top !important;
}
.cmnt_del:hover{
	background: url(data:image/gif;base64,) -78px bottom !important;
}
.cmnt_edit{
	background: url(data:image/gif;base64,) -52px top !important;
}
.cmnt_edit:hover{
	background: url(data:image/gif;base64,) -52px bottom !important;
}
.panel{
	margin-bottom:15px !important;
	border: 1px solid #c8c8c8 !important;
}
.panel_top {
	background: url(data:image/gif;base64,R0lGODlhAQBUAMQAAAAAAP////D5//r9/+r3/+34/9jx/+T1/+b2/+f2/+n3/+z4/+75/+/5//H6/+X2/+f3/+j3/+v4//D6//n9//X8//j9/////wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABcALAAAAAABAFQAAAUroDNNgtCcTFqsy+G+8DvMVG3XVm5VfO/3EgJBoYhEIIkEAvF4HAzQqDQaAgA7) repeat-x bottom left !important;
	border-bottom:1px solid #d8d8d8 !important;
	padding: 0 7px 0 0 !important;
	height:28px !important;
	text-align:right !important;
}
.panel_top span{
	padding: 6px 7px 5px 0 !important;
	direction: rtl !important;
	float:right !important;
}
.dpanel{
	 margin-bottom:15px !important;
	 border:1px solid #c8c8c8 !important;
	 border-top:none !important;
}
.dpanel_top{
	background: url(data:image/gif;base64,R0lGODlhAQBUAMQAAAAAAP////D5//r9/+r3/+34/9jx/+T1/+b2/+f2/+n3/+z4/+75/+/5//H6/+X2/+f3/+j3/+v4//D6//n9//X8//j9/////wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABcALAAAAAABAFQAAAUroDNNgtCcTFqsy+G+8DvMVG3XVm5VfO/3EgJBoYhEIIkEAvF4HAzQqDQaAgA7) repeat-x bottom left !important;
	border-top:1px solid #c8c8c8 !important;
	border-bottom:1px solid #c8c8c8 !important;
	padding-top: 7px !important;
	height:20px !important;
	text-align:center !important;
	float: left !important;
	width: 129px !important;
	cursor: pointer !important;
	font-size: 8pt !important;
}
.dpanel_top:hover{
	background: url(data:image/gif;base64,R0lGODlhAQBUAMQAAAAAAP////D5//r9/+r3/+34/9jx/+T1/+b2/+f2/+n3/+z4/+75/+/5//H6/+X2/+f3/+j3/+v4//D6//n9//X8//j9/////wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABcALAAAAAABAFQAAAUroDNNgtCcTFqsy+G+8DvMVG3XVm5VfO/3EgJBoYhEIIkEAvF4HAzQqDQaAgA7) repeat-x top left !important;
}
.PanelSelected{
	background: url(data:image/gif;base64,R0lGODlhAQBUAMQAAAAAAP////D5//r9/+r3/+34/9jx/+T1/+b2/+f2/+n3/+z4/+75/+/5//H6/+X2/+f3/+j3/+v4//D6//n9//X8//j9/////wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABcALAAAAAABAFQAAAUroDNNgtCcTFqsy+G+8DvMVG3XVm5VfO/3EgJBoYhEIIkEAvF4HAzQqDQaAgA7) repeat-x center left !important;
	border-top:1px solid #c8c8c8 !important;
	border-bottom:1px solid #f5fcff !important;
	padding-top: 7px !important;
	height:20px !important;
	text-align:center !important;
	float: left !important;
	width: 129px !important;
	font-size: 8pt !important;
	cursor: default !important;
}
.dpanel_sep{
	width: 6px !important;
	border:1px solid #c8c8c8 !important;
	border-top: none !important;
	height: 28px !important;
	float: left !important;
}
.panel_mid {
	background: #f5fcff !important;
	border-top: none !important;
	direction: rtl !important;
	padding: 10px 10px !important;
}
.panel_bot {
	border-top: 1px solid #d8d8d8 !important;
	background: #eaf7ff !important;
	height: 28px !important;
}
.info{
	border: 1px #c8c8c8 solid !important;
	background: #f5fcff !important;
	margin-bottom:10px !important;
	padding:5px 10px !important;
	direction:rtl !important;
	line-height:20px !important;
	text-align:justify !important;
	font-size:11px !important;
	color: #5e5e5e !important;
}
.userAward{
	border:1px solid #c8c8c8 !important;
	background: #f5fcff url("data:image/png !important;base64,") no-repeat 245px 6px !important;
	padding: 7px 30px 7px 0 !important;
	margin-bottom: 15px !important;
	direction: rtl !important;
}
#sep {
	width: 14px !important;
	cursor: pointer !important;
	background: url(data:image/gif;base64,) repeat-y 0 0 !important;
}
#sep:hover{
	background: url(data:image/gif;base64,) repeat-y right top !important;
}
.forumPost{
	background: url(data:image/gif;base64,R0lGODlhAQAlALMAAAAAAP///+33/u/4/uz3/uLz/e34/u74/vD5/u/5/vr6+vn5+fj4+Pf39/b29v///yH5BAEAAA8ALAAAAAABACUAAAQUEMk0xrnGCEKK/2DgOE3DnEuqKBEAOw==) repeat-x 0 0 !important;
	height:24px !important;
	line-height:24px !important;
	padding:0 5px !important;
	border:1px solid #c9c9c9 !important;
}

}
