css:
@namespace url(http://www.w3.org/1999/xhtml) !important;

@-moz-document domain("donbaleh.com") {

.comment{
	border:1px #e2b7b7 solid !important;
	margin-bottom: 10px !important;
	line-height: 20px !important;
}
.comment-top{
	border-bottom:1px #e2b7b7 solid !important;
	overflow:hidden !important;
	background:url("data:image/gif !important;base64,R0lGODlhAQAZAKIAAAAAAP/////k5P/p6f/q6v/r6////wAAACH5BAEAAAYALAAAAAABABkAAAMJWLrU3mOISWkCADs=") repeat-x !important;
}
.comment-bd{
	background:url("data:image/gif !important;base64,R0lGODlhAQAFAKIAAAAAAP/////p6f/s7P/w8P/z8//39////yH5BAEAAAcALAAAAAABAAUAAAMEKENlCQA7") repeat-x !important;
	padding: 5px !important;
	font-size:11px !important;
}
.cmnt_agree{
	background: url("data:image/gif !important;base64,") left top !important;
}
.cmnt_agree:hover{
	background: url("data:image/gif !important;base64,") left bottom !important;
}
.cmnt_point{
	background: #fff !important;
	text-align: center !important;
	font-size: 11px !important;
	direction: ltr !important;
	vertical-align: middle !important;
	border-right: 1px #e2b7b7 solid !important;
}
.cmnt_disagree{
	background: url("data:image/gif !important;base64,") -26px top !important;
}
.cmnt_disagree:hover{
	background: url("data:image/gif !important;base64,") -26px bottom !important;
}
.cmnt_quote{
	background: url("data:image/gif !important;base64,") right top !important;
}
.cmnt_quote:hover{
	background: url("data:image/gif !important;base64,") right bottom !important;
}
.cmnt_del{
	background: url("data:image/gif !important;base64,") -78px top !important;
}
.cmnt_del:hover{
	background: url("data:image/gif !important;base64,") -78px bottom !important;
}
.cmnt_edit{
	background: url("data:image/gif !important;base64,") -52px top !important;
}
.cmnt_edit:hover{
	background: url("data:image/gif !important;base64,") -52px bottom !important;
}
.panel{
	margin-bottom:15px !important;
	border: 1px solid #ffb9b9 !important;
}
.panel_top {
	background: url("data:image/gif !important;base64,R0lGODlhAQBUAMQAAAAAAP/////Pz//d3f/e3v/f3//g4P/h4f/i4v/j4//k5P/l5f/m5v/o6P/p6f/q6v/r6//s7P/t7f/y8v/29v/39//4+P///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABcALAAAAAABAFQAAAUroCRFJAQ9qOM07OC+8GvNc2XfFaVPfO/3jMVCoUgkEIeDwVAoEATQqDQaAgA7") repeat-x bottom left !important;
	border-bottom:1px solid #ffb9b9 !important;
	padding: 0 7px 0 0 !important;
	height:28px !important;
	text-align:right !important;
}
.panel_top span{
	padding: 6px 7px 5px 0 !important;
	direction: rtl !important;
	float:right !important;
}
.dpanel{
	 margin-bottom:15px !important;
	 border:1px solid #ffb9b9 !important;
	 border-top:none !important;
}
.dpanel_top{
	background: url("data:image/gif !important;base64,R0lGODlhAQBUAMQAAAAAAP/////Pz//d3f/e3v/f3//g4P/h4f/i4v/j4//k5P/l5f/m5v/o6P/p6f/q6v/r6//s7P/t7f/y8v/29v/39//4+P///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABcALAAAAAABAFQAAAUroCRFJAQ9qOM07OC+8GvNc2XfFaVPfO/3jMVCoUgkEIeDwVAoEATQqDQaAgA7") repeat-x bottom left !important;
	border-top:1px solid #ffb9b9 !important;
	border-bottom:1px solid #ffb9b9 !important;
	padding-top: 7px !important;
	height:20px !important;
	text-align:center !important;
	float: left !important;
	width: 129px !important;
	cursor: pointer !important;
	font-size: 8pt !important;
}
.dpanel_top:hover{
	background: url("data:image/gif !important;base64,R0lGODlhAQBUAMQAAAAAAP/////Pz//d3f/e3v/f3//g4P/h4f/i4v/j4//k5P/l5f/m5v/o6P/p6f/q6v/r6//s7P/t7f/y8v/29v/39//4+P///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABcALAAAAAABAFQAAAUroCRFJAQ9qOM07OC+8GvNc2XfFaVPfO/3jMVCoUgkEIeDwVAoEATQqDQaAgA7") repeat-x top left !important;
}
.PanelSelected{
	background: url("data:image/gif !important;base64,R0lGODlhAQBUAMQAAAAAAP/////Pz//d3f/e3v/f3//g4P/h4f/i4v/j4//k5P/l5f/m5v/o6P/p6f/q6v/r6//s7P/t7f/y8v/29v/39//4+P///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABcALAAAAAABAFQAAAUroCRFJAQ9qOM07OC+8GvNc2XfFaVPfO/3jMVCoUgkEIeDwVAoEATQqDQaAgA7") repeat-x center left !important;
	border-top:1px solid #ffb9b9 !important;
	border-bottom:1px solid #fff2f2 !important;
	padding-top: 7px !important;
	height:20px !important;
	text-align:center !important;
	float: left !important;
	width: 129px !important;
	font-size: 8pt !important;
	cursor: default !important;
}
.dpanel_sep{
	width: 6px !important;
	border:1px solid #ffb9b9 !important;
	border-top: none !important;
	height: 28px !important;
	float: left !important;
}
.panel_mid {
	background: #fff2f2 !important;
	border-top: none !important;
	direction: rtl !important;
	padding: 10px 10px !important;
}
.panel_bot {
	border-top: 1px solid #ffcfcf !important;
	background: #ffe7e7 !important;
	height: 28px !important;
}
.info{
	border: 1px #ffb9b9 solid !important;
	background: #fff2f2 !important;
	margin-bottom:10px !important;
	padding:5px 10px !important;
	direction:rtl !important;
	line-height:20px !important;
	text-align:justify !important;
	font-size:11px !important;
	color: #5e5e5e !important;
}
.userAward{
	border:1px solid #ffb9b9 !important;
	background: #fff2f2 url("data:image/png !important;base64,") no-repeat 245px 6px !important;
	padding: 7px 30px 7px 0 !important;
	margin-bottom: 15px !important;
	direction: rtl !important;
}
#sep {
	width: 14px !important;
	cursor: pointer !important;
	background: url("data:image/gif !important;base64,") repeat-y 0 0 !important;
}
#sep:hover{
	background: url("data:image/gif !important;base64,") repeat-y right top !important;
}
.forumPost{
	background: url("data:image/gif !important;base64,R0lGODlhAQAlALMAAAAAAP/////k5P/p6f/q6v/r6/r6+vn5+fj4+Pf39/b29v///wAAAAAAAAAAAAAAACH5BAEAAAsALAAAAAABACUAAAQSsEhJqq1jiM15UEqSIORhGkYEADs=") repeat-x 0 0 !important;
	height:24px !important;
	line-height:24px !important;
	padding:0 5px !important;
	border:1px solid #c9c9c9 !important;
}

}
