css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://muro.deviantart.com/") {
body{padding-top:0px!important;}


.brushSelector{height:37px!important; position:fixed!important; bottom:-27px!important;z-index:999!important;width:100%!important;margin-left:auto!important; margin-right:auto!important;overflow:visible!important;margin-top:0!important;
-moz-transition:bottom 0.2s 0s!important;

}
.brushSelector:hover{height:37px!important;bottom:0!important;
-moz-transition:bottom 0.2s 0s!important;  
}

.scroller{width:750px!important;background: #f0f0f0 url(http://trezoid.webs.com/dAfiles/Muro/ArrowUp.png) center top no-repeat!important;margin: 0 auto!important;left:0!important; right:0!important;border: 1px solid #aaa!important; -moz-border-radius:5px!important;padding-top:10px!important;height:37px!important;z-index:990!important;}

.brushSelector:hover .scroller{padding-top:0!important;background: #f0f0f0 none!important;}

.brushCont{left:0!important;padding-left:10px!important;}

.buttonPositioner{position:fixed!important; right: 120px!important; top: -10px!important;z-index:990!important;width: 300px!important;}

.drawPlzControls, .centeringDiv{height: 1px!important;bottom:1px!important;z-index:9998!important;}
.drawPlzControls .divider{margin:0!important;}

.middleArea{padding-bottom:0px!important;bottom:0px!important;top:0px!important;margin-bottom:0!important;z-index:0!important;}

.wacomIndicator{display:none!important;}

.controlColumn:first-of-type{position:fixed!important;top:-100px!important;}

.fourSquares{left:-275px!important;position:fixed!important;bottom:5px!important;top:auto!important;width:10px;padding-right:10px!important;border: 1px solid #aaa!important; -moz-border-radius:5px!important;background:#f0f0f0 url(http://trezoid.webs.com/dAfiles/Muro/ArrowRight.png) right center no-repeat!important;padding-left:0px!important;
-moz-transition:left 0.5s 0.5s!important; z-index:999!important;
}

.fourSquares:hover{
-moz-transition:left 0.5s 0s!important;  

left:0px!important;background: #f0f0f0 none!important;}

.brushSliders{position:fixed!important; right:-270px!important; left:auto!important; top:auto!important; bottom:5px!important;background:#f0f0f0 url(http://trezoid.webs.com/dAfiles/Muro/ArrowLeft.png) left center no-repeat!important;border: 1px solid #aaa!important; -moz-border-radius:5px!important;padding-right:0px!important;padding-left:5px!important;
-moz-transition:right 0.5s 0.5s!important;  }

.brushSliders:hover{right:0!important;background: #f0f0f0 none!important;-moz-transition:right 0.5s 0s!important;  }


.divider{display:none!important;}

body{overflow:hidden!important;}

canvas{z-index:1!important;}

.sidebar{z-index:-1!important;}

}
